import React, { useState, useMemo } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  X, 
  Car, 
  DollarSign, 
  MessageCircle, 
  Check, 
  XCircle, 
  ArrowLeftRight,
  User,
  Calendar,
  AlertTriangle,
  Edit
} from 'lucide-react';
import { useApp } from '../../context/AppContext.tsx';
import { Trade, TradeHistoryItem } from '../../types/index.ts';

interface TradeDetailModalProps {
  trade: Trade;
  isOutbound: boolean;
  onClose: () => void;
  onCounterOffer?: (trade: Trade) => void;
}

export function TradeDetailModal({ trade, isOutbound, onClose, onCounterOffer }: TradeDetailModalProps) {
  const { state, updateTrade, sendMessage } = useApp();
  const [loading, setLoading] = useState(false);
  const [messageText, setMessageText] = useState('');
  const [showCounterOffer, setShowCounterOffer] = useState(false);
  const [counterVehicles, setCounterVehicles] = useState<string[]>([]);
  const [counterCash, setCounterCash] = useState(0);
  const [counterMessage, setCounterMessage] = useState('');

  const listing = state.allListings.find(l => l.id === trade.listingId);
  const targetVehicle = listing ? (listing as any).vehicle || state.vehicles.find(v => v.id === listing.vehicleId) : null;
  
  // Extract user IDs properly (handle both string IDs and populated objects)
  const otherUserId = isOutbound 
    ? (typeof trade.receiverUserId === 'string' ? trade.receiverUserId : (trade.receiverUserId as any)?._id || (trade.receiverUserId as any)?.id)
    : (typeof trade.offererUserId === 'string' ? trade.offererUserId : (trade.offererUserId as any)?._id || (trade.offererUserId as any)?.id);
  
  const otherUser = state.users.find(u => u.id === otherUserId);

  // Get vehicles involved in the trade
  const offererVehicles = trade.offererVehicleIds.map(vId => 
    state.vehicles.find(v => v.id === vId)
  ).filter(Boolean);

  const receiverVehicles = (trade.receiverVehicleIds || []).map(vId => 
    state.vehicles.find(v => v.id === vId)
  ).filter(Boolean);

  // Calculate total values
  const offererValue = trade.offererCashAmount + 
    offererVehicles.reduce((sum, v) => sum + (v?.customPrice || v?.estimatedValue || 0), 0);

  const receiverValue = (trade.receiverCashAmount || 0) + 
    receiverVehicles.reduce((sum, v) => sum + (v?.customPrice || v?.estimatedValue || 0), 0);

  // Available vehicles for counter-offer (user's own vehicles that aren't listed/auctioned)
  const availableVehicles = state.vehicles.filter(vehicle => {
    if (vehicle.ownerId !== state.currentUser?.id) return false;
    
    // Check if vehicle is listed
    const isListed = state.listings.some(listing => listing.vehicleId === vehicle.id) ||
                    state.allListings.some(listing => listing.vehicleId === vehicle.id);
    
    // Check if vehicle is in auction
    const isInAuction = state.auctions.some(auction => auction.vehicleId === vehicle.id);
    
    return !isListed && !isInAuction;
  });

  const formatTimeAgo = (dateString: string) => {
    try {
      const now = new Date();
      const date = new Date(dateString);
      
      // Check if date is valid
      if (isNaN(date.getTime())) {
        console.warn('Invalid date string:', dateString);
        return 'Unknown';
      }
      
      const diffInMinutes = Math.floor((now.getTime() - date.getTime()) / (1000 * 60));
      
      if (diffInMinutes < 1) return 'Just now';
      if (diffInMinutes < 60) return `${diffInMinutes}m ago`;
      
      const diffInHours = Math.floor(diffInMinutes / 60);
      if (diffInHours < 24) return `${diffInHours}h ago`;
      
      const diffInDays = Math.floor(diffInHours / 24);
      if (diffInDays < 7) return `${diffInDays}d ago`;
      if (diffInDays < 30) return `${Math.floor(diffInDays / 7)}w ago`;
      
      return date.toLocaleDateString();
    } catch (error) {
      console.error('Error formatting date:', error, dateString);
      return 'Unknown';
    }
  };

  const handleAcceptTrade = async () => {
    setLoading(true);
    try {
      let updatedTrade;
      
      console.log('🔄 Accepting trade:', {
        tradeId: trade.id,
        currentStatus: trade.status,
        isOutbound,
        offererAccepted: trade.offererAccepted,
        receiverAccepted: trade.receiverAccepted
      });
      
      if (isOutbound) {
        // Outbound trade acceptance means offerer is accepting
        updatedTrade = {
          ...trade,
          offererAccepted: true,
          status: trade.receiverAccepted ? 'pending_acceptance' : 'accepted',
          updatedAt: new Date().toISOString(),
        };
      } else {
        // Inbound trade acceptance means receiver is accepting
        updatedTrade = {
          ...trade,
          receiverAccepted: true,
          status: trade.offererAccepted ? 'pending_acceptance' : 'accepted',
          updatedAt: new Date().toISOString(),
        };
      }
      
      console.log('✅ Updated trade:', {
        newStatus: updatedTrade.status,
        offererAccepted: updatedTrade.offererAccepted,
        receiverAccepted: updatedTrade.receiverAccepted
      });
      
      await updateTrade(updatedTrade);
      onClose();
    } catch (error) {
      console.error('Error accepting trade:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleRejectTrade = async () => {
    setLoading(true);
    try {
      const updatedTrade = {
        ...trade,
        status: 'rejected' as const,
        updatedAt: new Date().toISOString(),
        tradeHistory: [
          ...trade.tradeHistory,
          {
            id: `history_${Date.now()}`,
            action: 'rejected' as const,
            userId: state.currentUser!.id,
            timestamp: new Date().toISOString(),
            offererCashAmount: trade.offererCashAmount,
            offererVehicleIds: trade.offererVehicleIds,
            receiverCashAmount: trade.receiverCashAmount,
            receiverVehicleIds: trade.receiverVehicleIds
          }
        ]
      };
      await updateTrade(updatedTrade);
      onClose();
    } catch (error) {
      console.error('Error rejecting trade:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCancelTrade = async () => {
    setLoading(true);
    try {
      const updatedTrade = {
        ...trade,
        status: 'cancelled' as const,
        updatedAt: new Date().toISOString(),
        tradeHistory: [
          ...trade.tradeHistory,
          {
            id: `history_${Date.now()}`,
            action: 'cancelled' as const,
            userId: state.currentUser!.id,
            timestamp: new Date().toISOString(),
            offererCashAmount: trade.offererCashAmount,
            offererVehicleIds: trade.offererVehicleIds,
            receiverCashAmount: trade.receiverCashAmount,
            receiverVehicleIds: trade.receiverVehicleIds
          }
        ]
      };
      await updateTrade(updatedTrade);
      onClose();
    } catch (error) {
      console.error('Error cancelling trade:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSendMessage = async () => {
    if (!messageText.trim()) return;

    setLoading(true);
    try {
      await sendMessage({
        senderId: state.currentUser!.id,
        receiverId: otherUserId,
        tradeId: trade.id,
        content: messageText
      });
      setMessageText('');
    } catch (error) {
      console.error('Error sending message:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCounterOffer = () => {
    if (onCounterOffer) {
      onCounterOffer(trade);
    }
    onClose();
  };

  const handleVehicleToggle = (vehicleId: string) => {
    setCounterVehicles(prev => 
      prev.includes(vehicleId) 
        ? prev.filter(id => id !== vehicleId)
        : [...prev, vehicleId]
    );
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'pending': return 'text-yellow-400';
      case 'accepted': return 'text-green-400';
      case 'pending_acceptance': return 'text-blue-400';
      case 'rejected': return 'text-red-400';
      case 'completed': return 'text-blue-400';
      case 'cancelled': return 'text-gray-400';
      case 'countered': return 'text-purple-400';
      case 'declined': return 'text-red-400';
      default: return 'text-primary-300';
    }
  };

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50"
      onClick={(e) => e.target === e.currentTarget && onClose()}
    >
      <motion.div
        initial={{ scale: 0.9, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        exit={{ scale: 0.9, opacity: 0 }}
        className="glass-effect rounded-2xl w-full max-w-6xl max-h-[90vh] overflow-y-auto"
      >
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-primary-700/30">
          <div>
            <h1 className="text-2xl font-bold text-primary-100">Trade Details</h1>
            <div className="flex items-center space-x-2 mt-1">
              <span className={`text-sm font-medium capitalize ${getStatusColor(trade.status)}`}>
                {trade.status}
              </span>
              <span className="text-primary-400">•</span>
              <span className="text-sm text-primary-400">{formatTimeAgo(trade.updatedAt)}</span>
            </div>
          </div>
          <button
            onClick={onClose}
            className="p-2 hover:bg-primary-800/50 rounded-full transition-colors"
          >
            <X className="w-6 h-6 text-primary-300" />
          </button>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 p-6">
          {/* Left Column - Trade Details */}
          <div className="space-y-6">
            {/* Target Listing */}
            {listing && (
              <div>
                <h3 className="text-lg font-semibold text-primary-100 mb-4">Target Listing</h3>
                <div className="glass-effect rounded-xl p-4">
                  <div className="flex items-center space-x-4">
                    <div className="w-16 h-16 bg-primary-800/30 rounded-lg flex items-center justify-center">
                      {targetVehicle?.images?.[0] ? (
                        <img
                          src={targetVehicle.images[0]}
                          alt={listing.title}
                          className="w-full h-full object-cover rounded-lg"
                        />
                      ) : (
                        <Car className="w-8 h-8 text-primary-400" />
                      )}
                    </div>
                    <div className="flex-1">
                      <h4 className="font-semibold text-primary-100">{listing.title}</h4>
                      <p className="text-sm text-primary-300">
                        {targetVehicle && `${targetVehicle.year} ${targetVehicle.make} ${targetVehicle.model}`}
                      </p>
                      <p className="text-green-400 font-bold">
                        ${listing.price.toLocaleString()}
                      </p>
                    </div>
                  </div>
                </div>
              </div>
            )}

            {/* Offerer's Offer */}
            <div>
              <h3 className="text-lg font-semibold text-primary-100 mb-4">
                {isOutbound ? 'Your Offer' : 'Their Offer'}
              </h3>
              <div className="glass-effect rounded-xl p-4 space-y-4">
                {/* Vehicles */}
                {offererVehicles.length > 0 && (
                  <div>
                    <h4 className="font-medium text-primary-200 mb-2">
                      Vehicles ({offererVehicles.length})
                    </h4>
                    <div className="space-y-2">
                      {offererVehicles.map((vehicle) => (
                        <div key={vehicle?.id} className="flex items-center space-x-3 bg-primary-800/20 rounded-lg p-3">
                          <div className="w-10 h-10 bg-primary-800/30 rounded-lg flex items-center justify-center">
                            {vehicle?.images?.[0] ? (
                              <img
                                src={vehicle.images[0]}
                                alt={`${vehicle.year} ${vehicle.make} ${vehicle.model}`}
                                className="w-full h-full object-cover rounded-lg"
                              />
                            ) : (
                              <Car className="w-5 h-5 text-primary-400" />
                            )}
                          </div>
                          <div className="flex-1">
                            <p className="font-medium text-primary-100">
                              {vehicle?.year} {vehicle?.make} {vehicle?.model}
                            </p>
                            <p className="text-sm text-primary-300">
                              {vehicle?.mileage.toLocaleString()} mi
                            </p>
                          </div>
                          <p className="text-green-400 font-medium">
                            ${(vehicle?.customPrice || vehicle?.estimatedValue || 0).toLocaleString()}
                          </p>
                        </div>
                      ))}
                    </div>
                  </div>
                )}

                {/* Cash */}
                {trade.offererCashAmount !== 0 && (
                  <div className="flex items-center justify-between bg-primary-800/20 rounded-lg p-3">
                    <div className="flex items-center space-x-2">
                      <DollarSign className="w-5 h-5 text-green-400" />
                      <span className="text-primary-200">Cash</span>
                    </div>
                    <span className={`font-bold ${trade.offererCashAmount >= 0 ? 'text-green-400' : 'text-red-400'}`}>
                      {trade.offererCashAmount >= 0 ? '+' : ''}${trade.offererCashAmount.toLocaleString()}
                    </span>
                  </div>
                )}

                {/* Total */}
                <div className="border-t border-primary-700/30 pt-3">
                  <div className="flex items-center justify-between">
                    <span className="font-semibold text-primary-100">Total Value:</span>
                    <span className="text-green-400 font-bold text-lg">
                      ${offererValue.toLocaleString()}
                    </span>
                  </div>
                </div>

                {/* Message */}
                {trade.message && (
                  <div className="border-t border-primary-700/30 pt-3">
                    <p className="text-sm text-primary-300 mb-1">Message:</p>
                    <p className="text-primary-100 italic">"{trade.message}"</p>
                  </div>
                )}
              </div>
            </div>

            {/* Counter Offer (if exists) */}
            {trade.status === 'countered' && (
              <div>
                <h3 className="text-lg font-semibold text-primary-100 mb-4">
                  {isOutbound ? 'Their Counter Offer' : 'Your Counter Offer'}
                </h3>
                <div className="glass-effect rounded-xl p-4 space-y-4 border border-purple-500/30">
                  {/* Counter Vehicles */}
                  {receiverVehicles.length > 0 && (
                    <div>
                      <h4 className="font-medium text-primary-200 mb-2">
                        Vehicles ({receiverVehicles.length})
                      </h4>
                      <div className="space-y-2">
                        {receiverVehicles.map((vehicle) => (
                          <div key={vehicle?.id} className="flex items-center space-x-3 bg-purple-500/10 rounded-lg p-3">
                            <div className="w-10 h-10 bg-primary-800/30 rounded-lg flex items-center justify-center">
                              {vehicle?.images?.[0] ? (
                                <img
                                  src={vehicle.images[0]}
                                  alt={`${vehicle.year} ${vehicle.make} ${vehicle.model}`}
                                  className="w-full h-full object-cover rounded-lg"
                                />
                              ) : (
                                <Car className="w-5 h-5 text-primary-400" />
                              )}
                            </div>
                            <div className="flex-1">
                              <p className="font-medium text-primary-100">
                                {vehicle?.year} {vehicle?.make} {vehicle?.model}
                              </p>
                              <p className="text-sm text-primary-300">
                                {vehicle?.mileage.toLocaleString()} mi
                              </p>
                            </div>
                            <p className="text-purple-400 font-medium">
                              ${(vehicle?.customPrice || vehicle?.estimatedValue || 0).toLocaleString()}
                            </p>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}

                  {/* Counter Cash */}
                  {(trade.receiverCashAmount || 0) !== 0 && (
                    <div className="flex items-center justify-between bg-purple-500/10 rounded-lg p-3">
                      <div className="flex items-center space-x-2">
                        <DollarSign className="w-5 h-5 text-purple-400" />
                        <span className="text-primary-200">Cash</span>
                      </div>
                      <span className={`font-bold ${(trade.receiverCashAmount || 0) >= 0 ? 'text-purple-400' : 'text-red-400'}`}>
                        {(trade.receiverCashAmount || 0) >= 0 ? '+' : ''}${(trade.receiverCashAmount || 0).toLocaleString()}
                      </span>
                    </div>
                  )}

                  {/* Counter Total */}
                  <div className="border-t border-purple-500/30 pt-3">
                    <div className="flex items-center justify-between">
                      <span className="font-semibold text-primary-100">Counter Value:</span>
                      <span className="text-purple-400 font-bold text-lg">
                        ${receiverValue.toLocaleString()}
                      </span>
                    </div>
                  </div>

                  {/* Counter Message */}
                  {trade.counterMessage && (
                    <div className="border-t border-purple-500/30 pt-3">
                      <p className="text-sm text-primary-300 mb-1">Counter Message:</p>
                      <p className="text-primary-100 italic">"{trade.counterMessage}"</p>
                    </div>
                  )}
                </div>
              </div>
            )}
          </div>

          {/* Right Column - Actions & Communication */}
          <div className="space-y-6">
            {/* Other User Info */}
            <div>
              <h3 className="text-lg font-semibold text-primary-100 mb-4">
                {isOutbound ? 'Receiver' : 'Offerer'}
              </h3>
              <div className="glass-effect rounded-xl p-4">
                <div className="flex items-center space-x-4">
                  <div className="w-12 h-12 bg-primary-800/50 rounded-full flex items-center justify-center">
                    {otherUser?.avatar ? (
                      <img
                        src={otherUser.avatar}
                        alt={otherUser.username}
                        className="w-full h-full object-cover rounded-full"
                      />
                    ) : (
                      <User className="w-6 h-6 text-primary-300" />
                    )}
                  </div>
                  <div>
                    <h4 className="font-semibold text-primary-100">@{otherUser?.username || 'Unknown'}</h4>
                    <div className="flex items-center space-x-2 text-sm text-primary-300">
                      {otherUser?.rating ? (
                        <>
                          <span>{otherUser.rating.toFixed(1)} ⭐</span>
                          <span>({otherUser.reviewCount || 0} reviews)</span>
                        </>
                      ) : (
                        <span>No reviews yet</span>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            </div>

            {/* Actions */}
            {trade.status === 'pending' && (
              <div>
                <h3 className="text-lg font-semibold text-primary-100 mb-4">Actions</h3>
                <div className="space-y-3">
                  {isOutbound ? (
                    // Outbound trade actions
                    <button
                      onClick={handleCancelTrade}
                      disabled={loading}
                      className="btn-secondary w-full flex items-center justify-center gap-2"
                    >
                      <XCircle className="w-5 h-5" />
                      Cancel Trade Offer
                    </button>
                  ) : (
                    // Inbound trade actions
                    <>
                      <button
                        onClick={handleAcceptTrade}
                        disabled={loading}
                        className="btn-primary w-full flex items-center justify-center gap-2"
                      >
                        <Check className="w-5 h-5" />
                        Accept Trade
                      </button>
                      <button
                        onClick={() => setShowCounterOffer(!showCounterOffer)}
                        className="btn-secondary w-full flex items-center justify-center gap-2"
                      >
                        <ArrowLeftRight className="w-5 h-5" />
                        Counter Offer
                      </button>
                      <button
                        onClick={handleRejectTrade}
                        disabled={loading}
                        className="btn-secondary w-full flex items-center justify-center gap-2 text-red-400 hover:text-red-300"
                      >
                        <XCircle className="w-5 h-5" />
                        Reject Trade
                      </button>
                    </>
                  )}
                </div>
              </div>
            )}

            {/* Counter Offer Form */}
            {showCounterOffer && !isOutbound && trade.status === 'pending' && (
              <div>
                <h3 className="text-lg font-semibold text-primary-100 mb-4">Make Counter Offer</h3>
                <div className="glass-effect rounded-xl p-4 space-y-4">
                  {/* Counter Vehicles */}
                  <div>
                    <h4 className="font-medium text-primary-200 mb-2">Your Vehicles</h4>
                    {availableVehicles.length === 0 ? (
                      <p className="text-sm text-primary-400">No available vehicles</p>
                    ) : (
                      <div className="space-y-2 max-h-40 overflow-y-auto">
                        {availableVehicles.map((vehicle) => (
                          <div
                            key={vehicle.id}
                            onClick={() => handleVehicleToggle(vehicle.id)}
                            className={`flex items-center space-x-3 p-3 rounded-lg cursor-pointer transition-all ${
                              counterVehicles.includes(vehicle.id)
                                ? 'bg-blue-500/20 border border-blue-500/50'
                                : 'bg-primary-800/20 hover:bg-primary-800/30'
                            }`}
                          >
                            <div className="w-8 h-8 bg-primary-800/30 rounded-lg flex items-center justify-center">
                              <Car className="w-4 h-4 text-primary-400" />
                            </div>
                            <div className="flex-1">
                              <p className="text-sm font-medium text-primary-100">
                                {vehicle.year} {vehicle.make} {vehicle.model}
                              </p>
                              <p className="text-xs text-primary-300">
                                ${(vehicle.customPrice || vehicle.estimatedValue).toLocaleString()}
                              </p>
                            </div>
                            {counterVehicles.includes(vehicle.id) && (
                              <Check className="w-4 h-4 text-blue-400" />
                            )}
                          </div>
                        ))}
                      </div>
                    )}
                  </div>

                  {/* Counter Cash */}
                  <div>
                    <h4 className="font-medium text-primary-200 mb-2">Cash Amount</h4>
                    <input
                      type="number"
                      value={counterCash === 0 ? '' : counterCash}
                      onChange={(e) => setCounterCash(Number(e.target.value) || 0)}
                      placeholder="0"
                      className="input-field"
                    />
                  </div>

                  {/* Counter Message */}
                  <div>
                    <h4 className="font-medium text-primary-200 mb-2">Message</h4>
                    <textarea
                      value={counterMessage}
                      onChange={(e) => setCounterMessage(e.target.value)}
                      placeholder="Explain your counter offer..."
                      className="input-field h-20 resize-none"
                    />
                  </div>

                  <button
                    onClick={handleCounterOffer}
                    disabled={loading}
                    className="btn-primary w-full"
                  >
                    Send Counter Offer
                  </button>
                </div>
              </div>
            )}

            {/* Messaging */}
            <div>
              <h3 className="text-lg font-semibold text-primary-100 mb-4">Send Message</h3>
              <div className="glass-effect rounded-xl p-4 space-y-4">
                <textarea
                  value={messageText}
                  onChange={(e) => setMessageText(e.target.value)}
                  placeholder="Send a message about this trade..."
                  className="input-field h-20 resize-none"
                />
                <button
                  onClick={handleSendMessage}
                  disabled={loading || !messageText.trim()}
                  className="btn-primary w-full flex items-center justify-center gap-2"
                >
                  <MessageCircle className="w-5 h-5" />
                  Send Message
                </button>
                <p className="text-xs text-primary-400">
                  Messages will be marked as trade-related and appear in your Messages tab.
                </p>
              </div>
            </div>

            {/* Trade History */}
            <div>
              <h3 className="text-lg font-semibold text-primary-100 mb-4">Trade History</h3>
              <div className="glass-effect rounded-xl p-4">
                <div className="space-y-3">
                  {trade.tradeHistory.map((item, index) => {
                    // Simplified user lookup
                    const user = state.users.find(u => u.id === item.userId) || {
                      username: 'Unknown'
                    };

                    return (
                      <div key={item.id || index} className="flex items-start space-x-3">
                        <div className="w-6 h-6 bg-primary-800/50 rounded-full flex items-center justify-center mt-1">
                          <Calendar className="w-3 h-3 text-primary-400" />
                        </div>
                        <div className="flex-1">
                          <p className="text-sm text-primary-200">
                            <span className="font-medium">@{user.username}</span>
                            <span className="text-primary-400 ml-1">
                              {item.action === 'created' && 'created the trade offer'}
                              {item.action === 'countered' && 'made a counter offer'}
                              {item.action === 'accepted' && 'accepted the trade'}
                              {item.action === 'rejected' && 'rejected the trade'}
                              {item.action === 'cancelled' && 'cancelled the trade'}
                              {item.action === 'completed' && 'completed the trade'}
                            </span>
                          </p>
                          <p className="text-xs text-primary-400">
                            {formatTimeAgo(item.timestamp)}
                          </p>
                          {item.message && (
                            <p className="text-sm text-primary-300 mt-1 italic">
                              "{item.message}"
                            </p>
                          )}
                        </div>
                      </div>
                    );
                  })}
                </div>
              </div>
            </div>
          </div>
        </div>
      </motion.div>
    </motion.div>
  );
}
