import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Car, ArrowLeftRight, MessageCircle, User, LogOut, Menu, X, List, Gavel } from 'lucide-react';
import { useApp } from '../../context/AppContext.tsx';
import { NavigationTab } from '../../types/index.ts';

interface NavigationProps {
  isMobile: boolean;
  isOpen: boolean;
  onToggle: () => void;
}

export function Navigation({ isMobile, isOpen, onToggle }: NavigationProps) {
  const { activeTab, setActiveTab, logout, state } = useApp();
  const [isHovered, setIsHovered] = useState(false);

  const navItems: Array<{ tab: NavigationTab; icon: React.ReactNode; label: string; badge?: number }> = [
    {
      tab: 'garage',
      icon: <Car className="w-5 h-5" />,
      label: 'Garage',
    },
    {
      tab: 'listings',
      icon: <List className="w-5 h-5" />,
      label: 'Listings',
    },
    {
      tab: 'auctions',
      icon: <Gavel className="w-5 h-5" />,
      label: 'Auctions',
    },
    {
      tab: 'trades',
      icon: <ArrowLeftRight className="w-5 h-5" />,
      label: 'Trades',
      badge: state.trades.filter(t => t.status === 'pending').length || undefined,
    },
    {
      tab: 'messages',
      icon: <MessageCircle className="w-5 h-5" />,
      label: 'Messages',
      badge: state.conversations.reduce((sum, c) => sum + c.unreadCount, 0) || undefined,
    },
  ];

  const handleTabClick = (tab: NavigationTab) => {
    setActiveTab(tab);
    if (isMobile) {
      onToggle();
    }
  };

  const handleLogout = () => {
    if (window.confirm('Are you sure you want to logout?')) {
      logout();
      if (isMobile) {
        onToggle();
      }
    }
  };

  const isCollapsed = !isHovered && !isMobile;

  if (isMobile) {
    return (
      <>
        {/* Mobile Navigation Toggle */}
        <motion.button
          whileTap={{ scale: 0.95 }}
          onClick={onToggle}
          className="fixed top-4 left-4 z-50 p-3 bg-primary-500 text-white rounded-full shadow-lg lg:hidden"
        >
          {isOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
        </motion.button>

        {/* Mobile Navigation Overlay */}
        {isOpen && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-black/50 z-40 lg:hidden"
            onClick={onToggle}
          />
        )}

        {/* Mobile Navigation Sidebar */}
        <motion.div
          initial={{ x: '-100%' }}
          animate={{ x: isOpen ? 0 : '-100%' }}
          transition={{ type: 'spring', damping: 25, stiffness: 200 }}
          className="fixed left-0 top-0 h-full w-72 glass-effect shadow-xl z-40 lg:hidden"
        >
          <div className="p-6">
            <div className="flex items-center space-x-3 mb-8 mt-12">
              <div className="w-12 h-12 bg-gradient-to-br from-primary-100 to-primary-200 rounded-full flex items-center justify-center">
                <Car className="w-6 h-6 text-primary-900" />
              </div>
              <div>
                <h2 className="font-bold text-xl text-primary-100">CarTrade</h2>
                <p className="text-sm text-primary-300">Vehicle Trading</p>
              </div>
            </div>

            <nav className="space-y-2">
              {navItems.map((item) => (
                <motion.button
                  key={item.tab}
                  whileTap={{ scale: 0.98 }}
                  onClick={() => handleTabClick(item.tab)}
                  className={`w-full flex items-center justify-between px-4 py-3 rounded-lg transition-colors ${
                    activeTab === item.tab
                      ? 'bg-primary-100/10 text-primary-100 border-l-4 border-primary-100'
                      : 'text-primary-300 hover:bg-primary-800/30 hover:text-primary-100'
                  }`}
                >
                  <div className="flex items-center space-x-3">
                    {item.icon}
                    <span className="font-medium">{item.label}</span>
                  </div>
                  {item.badge && item.badge > 0 && (
                    <span className="bg-red-500 text-white text-xs px-2 py-1 rounded-full min-w-[20px] text-center">
                      {item.badge > 99 ? '99+' : item.badge}
                    </span>
                  )}
                </motion.button>
              ))}
            </nav>

            <div className="mt-8 pt-4 border-t border-primary-700/30">
              <motion.button
                whileTap={{ scale: 0.98 }}
                onClick={() => handleTabClick('profile')}
                className={`w-full flex items-center space-x-3 px-4 py-3 mb-3 rounded-lg transition-colors ${
                  activeTab === 'profile'
                    ? 'bg-primary-100/10 text-primary-100 border-l-4 border-primary-100'
                    : 'text-primary-300 hover:bg-primary-800/30 hover:text-primary-100'
                }`}
              >
                <div className="w-10 h-10 bg-primary-800/50 rounded-full flex items-center justify-center">
                  <User className="w-5 h-5 text-primary-300" />
                </div>
                <div className="text-left">
                  <p className="font-medium text-primary-100">{state.currentUser?.username}</p>
                  <p className="text-sm text-primary-400">{state.currentUser?.email}</p>
                </div>
              </motion.button>

              <motion.button
                whileTap={{ scale: 0.98 }}
                onClick={handleLogout}
                className="w-full flex items-center space-x-3 px-4 py-3 text-red-400 hover:bg-red-500/10 rounded-lg transition-colors"
              >
                <LogOut className="w-5 h-5" />
                <span className="font-medium">Logout</span>
              </motion.button>
            </div>
          </div>
        </motion.div>
      </>
    );
  }

  // Desktop Navigation
  return (
    <motion.div
      initial={{ x: -20, opacity: 0 }}
      animate={{ x: 0, opacity: 1, width: isCollapsed ? 80 : 288 }}
      transition={{ duration: 0.3 }}
      className="fixed left-0 top-0 h-full glass-effect shadow-xl z-30 hidden lg:block"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className="p-6">
        <div className={`flex items-center ${isCollapsed ? 'justify-center' : 'justify-between'} mb-8`}>
          <div className={`flex items-center ${isCollapsed ? 'justify-center' : 'space-x-3'}`}>
            <div className="w-12 h-12 bg-gradient-to-br from-primary-100 to-primary-200 rounded-full flex items-center justify-center">
              <Car className="w-6 h-6 text-primary-900" />
            </div>
            {!isCollapsed && (
              <div>
                <h2 className="font-bold text-xl text-primary-100">CarTrade</h2>
                <p className="text-sm text-primary-300">Vehicle Trading</p>
              </div>
            )}
          </div>
        </div>

        <nav className="space-y-2">
          {navItems.map((item, index) => (
            <motion.button
              key={item.tab}
              initial={{ x: -20, opacity: 0 }}
              animate={{ x: 0, opacity: 1 }}
              transition={{ delay: index * 0.1 }}
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              onClick={() => handleTabClick(item.tab)}
              className={`w-full h-12 flex items-center ${isCollapsed ? 'justify-center px-3' : 'justify-between px-4'} py-3 rounded-lg transition-all duration-200 ${
                activeTab === item.tab
                  ? 'bg-primary-100/10 text-primary-100 border-l-4 border-primary-100 shadow-sm'
                  : 'text-primary-300 hover:bg-primary-800/30 hover:text-primary-100 hover:shadow-sm'
              }`}
              title={isCollapsed ? item.label : undefined}
            >
              <div className={`flex items-center ${isCollapsed ? 'justify-center' : 'space-x-3'}`}>
                {item.icon}
                {!isCollapsed && <span className="font-medium">{item.label}</span>}
              </div>
              {!isCollapsed && item.badge && item.badge > 0 && (
                <motion.span
                  initial={{ scale: 0 }}
                  animate={{ scale: 1 }}
                  className="bg-red-500 text-white text-xs px-2 py-1 rounded-full min-w-[20px] text-center animate-bounce-subtle"
                >
                  {item.badge > 99 ? '99+' : item.badge}
                </motion.span>
              )}
            </motion.button>
          ))}
        </nav>

        <div className="mt-auto pt-4 border-t border-primary-700/30">
          <motion.button
            whileTap={{ scale: 0.98 }}
            onClick={() => handleTabClick('profile')}
            className={`w-full h-12 flex items-center ${isCollapsed ? 'justify-center px-3' : 'space-x-3 px-4'} py-3 mb-3 rounded-lg transition-colors ${
              activeTab === 'profile'
                ? 'bg-primary-100/10 text-primary-100 border-l-4 border-primary-100'
                : 'text-primary-300 hover:bg-primary-800/30 hover:text-primary-100'
            }`}
            title={isCollapsed ? `${state.currentUser?.username} - Profile` : undefined}
          >
            <div className="w-10 h-10 bg-primary-800/50 rounded-full flex items-center justify-center flex-shrink-0">
              <User className="w-5 h-5 text-primary-300" />
            </div>
            {!isCollapsed && (
              <div className="flex-1 min-w-0 text-left">
                <p className="font-medium text-primary-100 truncate">{state.currentUser?.username}</p>
                <p className="text-sm text-primary-400 truncate">{state.currentUser?.email}</p>
              </div>
            )}
          </motion.button>

          <motion.button
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
            onClick={handleLogout}
            className={`w-full h-12 flex items-center ${isCollapsed ? 'justify-center px-3' : 'space-x-3 px-4'} py-3 text-red-400 hover:bg-red-500/10 rounded-lg transition-colors`}
            title={isCollapsed ? 'Logout' : undefined}
          >
            <LogOut className="w-5 h-5 flex-shrink-0" />
            {!isCollapsed && <span className="font-medium">Logout</span>}
          </motion.button>
        </div>
      </div>
    </motion.div>
  );
}
