import React, { useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { X, ChevronLeft, ChevronRight } from 'lucide-react';

interface ImageModalProps {
  isOpen: boolean;
  onClose: () => void;
  images: string[];
  currentIndex: number;
  onNavigate?: (index: number) => void;
  title?: string;
}

export function ImageModal({ 
  isOpen, 
  onClose, 
  images, 
  currentIndex, 
  onNavigate,
  title 
}: ImageModalProps) {
  // Handle keyboard navigation
  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if (!isOpen) return;
      
      switch (e.key) {
        case 'Escape':
          onClose();
          break;
        case 'ArrowLeft':
          if (onNavigate && currentIndex > 0) {
            onNavigate(currentIndex - 1);
          }
          break;
        case 'ArrowRight':
          if (onNavigate && currentIndex < images.length - 1) {
            onNavigate(currentIndex + 1);
          }
          break;
      }
    };

    if (isOpen) {
      document.addEventListener('keydown', handleKeyDown);
      document.body.style.overflow = 'hidden';
    }

    return () => {
      document.removeEventListener('keydown', handleKeyDown);
      document.body.style.overflow = 'unset';
    };
  }, [isOpen, currentIndex, images.length, onNavigate, onClose]);

  if (!isOpen || !images.length) return null;

  const currentImage = images[currentIndex];

  const handlePrevious = () => {
    if (onNavigate && currentIndex > 0) {
      onNavigate(currentIndex - 1);
    }
  };

  const handleNext = () => {
    if (onNavigate && currentIndex < images.length - 1) {
      onNavigate(currentIndex + 1);
    }
  };

  return (
    <AnimatePresence>
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        className="fixed inset-0 bg-black/95 backdrop-blur-sm flex items-center justify-center z-[9999]"
        onClick={onClose}
        style={{ margin: 0, padding: 0 }}
      >
        {/* Close Button */}
        <motion.button
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          exit={{ opacity: 0, scale: 0.8 }}
          onClick={onClose}
          className="absolute top-6 right-6 z-10 w-12 h-12 bg-black/50 hover:bg-black/70 border border-white/20 rounded-full flex items-center justify-center transition-all duration-200 backdrop-blur-sm"
        >
          <X className="w-6 h-6 text-white" />
        </motion.button>

        {/* Image Counter */}
        {images.length > 1 && (
          <motion.div
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            className="absolute top-6 left-1/2 transform -translate-x-1/2 z-10 bg-black/50 backdrop-blur-sm border border-white/20 text-white px-4 py-2 rounded-full text-sm font-medium"
          >
            {currentIndex + 1} of {images.length}
          </motion.div>
        )}

        {/* Title */}
        {title && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 20 }}
            className="absolute bottom-6 left-1/2 transform -translate-x-1/2 z-10 bg-black/50 backdrop-blur-sm border border-white/20 text-white px-6 py-3 rounded-full text-lg font-medium"
          >
            {title}
          </motion.div>
        )}

        {/* Main Image Container */}
        <motion.div
          initial={{ scale: 0.9, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          exit={{ scale: 0.9, opacity: 0 }}
          className="relative w-full h-full flex items-center justify-center p-20"
          onClick={(e) => e.stopPropagation()}
        >
          <img
            src={currentImage}
            alt={`Image ${currentIndex + 1}`}
            className="max-w-full max-h-full object-contain rounded-lg shadow-2xl"
            style={{ 
              maxWidth: 'calc(100vw - 10rem)', 
              maxHeight: 'calc(100vh - 10rem)',
              objectFit: 'contain'
            }}
          />

          {/* Navigation Arrows */}
          {images.length > 1 && onNavigate && (
            <>
              <motion.button
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: -20 }}
                onClick={handlePrevious}
                disabled={currentIndex === 0}
                className="absolute left-6 top-1/2 transform -translate-y-1/2 w-14 h-14 bg-black/50 hover:bg-black/70 disabled:opacity-30 disabled:cursor-not-allowed border border-white/20 rounded-full flex items-center justify-center transition-all duration-200 backdrop-blur-sm"
              >
                <ChevronLeft className="w-7 h-7 text-white" />
              </motion.button>
              <motion.button
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: 20 }}
                onClick={handleNext}
                disabled={currentIndex === images.length - 1}
                className="absolute right-6 top-1/2 transform -translate-y-1/2 w-14 h-14 bg-black/50 hover:bg-black/70 disabled:opacity-30 disabled:cursor-not-allowed border border-white/20 rounded-full flex items-center justify-center transition-all duration-200 backdrop-blur-sm"
              >
                <ChevronRight className="w-7 h-7 text-white" />
              </motion.button>
            </>
          )}
        </motion.div>

        {/* Thumbnail Strip */}
        {images.length > 1 && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 20 }}
            className="absolute bottom-20 left-1/2 transform -translate-x-1/2 z-10"
          >
            <div className="flex space-x-3 bg-black/50 backdrop-blur-sm border border-white/20 rounded-2xl p-4 max-w-screen-lg overflow-x-auto">
              {images.map((image, index) => (
                <motion.button
                  key={index}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  onClick={() => onNavigate?.(index)}
                  className={`flex-shrink-0 w-16 h-16 rounded-xl border-2 transition-all duration-200 overflow-hidden ${
                    index === currentIndex
                      ? 'border-blue-400 shadow-lg shadow-blue-400/50'
                      : 'border-white/30 hover:border-white/60'
                  }`}
                >
                  <img
                    src={image}
                    alt={`Thumbnail ${index + 1}`}
                    className="w-full h-full object-cover"
                  />
                </motion.button>
              ))}
            </div>
          </motion.div>
        )}
      </motion.div>
    </AnimatePresence>
  );
}
