import React, { useState, useMemo } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  X, 
  Car, 
  DollarSign, 
  MessageCircle, 
  Check, 
  XCircle, 
  ArrowLeftRight,
  User,
  Calendar,
  AlertTriangle,
  Edit
} from 'lucide-react';
import { useApp } from '../../context/AppContext.tsx';
import { Trade, TradeHistoryItem } from '../../types/index.ts';

interface TradeDetailModalProps {
  trade: Trade;
  isOutbound: boolean;
  onClose: () => void;
}

export function TradeDetailModal({ trade, isOutbound, onClose }: TradeDetailModalProps) {
  const { state, updateTrade, sendMessage } = useApp();
  const [showCounterOffer, setShowCounterOffer] = useState(false);
  const [counterCash, setCounterCash] = useState(0);
  const [counterVehicles, setCounterVehicles] = useState<string[]>([]);
  const [counterMessage, setCounterMessage] = useState('');
  const [messageText, setMessageText] = useState('');
  const [loading, setLoading] = useState(false);

  const listing = state.allListings.find(l => l.id === trade.listingId);
  const targetVehicle = listing ? (listing as any).vehicle || state.vehicles.find(v => v.id === listing.vehicleId) : null;
  const otherUserId = isOutbound ? trade.receiverUserId : trade.offererUserId;
  const otherUser = state.users.find(u => u.id === otherUserId);

  // Get vehicles involved in the trade
  const offererVehicles = trade.offererVehicleIds.map(vId => 
    state.vehicles.find(v => v.id === vId)
  ).filter(Boolean);

  const receiverVehicles = (trade.receiverVehicleIds || []).map(vId => 
    state.vehicles.find(v => v.id === vId)
  ).filter(Boolean);

  // Calculate total values
  const offererValue = trade.offererCashAmount + 
    offererVehicles.reduce((sum, v) => sum + (v?.customPrice || v?.estimatedValue || 0), 0);

  const receiverValue = (trade.receiverCashAmount || 0) + 
    receiverVehicles.reduce((sum, v) => sum + (v?.customPrice || v?.estimatedValue || 0), 0);

  // Available vehicles for counter-offer (only for receiver)
  const availableVehicles = useMemo(() => {
    if (isOutbound) return [];
    return state.vehicles.filter(vehicle => 
      !vehicle.isListed && !vehicle.isAuctioned && vehicle.ownerId === state.currentUser?.id
    );
  }, [state.vehicles, state.currentUser?.id, isOutbound]);

  const formatTimeAgo = (dateString: string) => {
    try {
      const now = new Date();
      const date = new Date(dateString);
      const diffInMinutes = Math.floor((now.getTime() - date.getTime()) / (1000 * 60));
      
      if (diffInMinutes < 1) return 'Just now';
      if (diffInMinutes < 60) return `${diffInMinutes}m ago`;
      
      const diffInHours = Math.floor(diffInMinutes / 60);
      if (diffInHours < 24) return `${diffInHours}h ago`;
      
      const diffInDays = Math.floor(diffInHours / 24);
      if (diffInDays < 7) return `${diffInDays}d ago`;
      if (diffInDays < 30) return `${Math.floor(diffInDays / 7)}w ago`;
      
      return date.toLocaleDateString();
    } catch (error) {
      return 'Unknown';
    }
  };

  const handleAcceptTrade = async () => {
    setLoading(true);
    try {
      await updateTrade({
        ...trade,
        status: 'accepted'
      });
      onClose();
    } catch (error) {
      console.error('Error accepting trade:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleRejectTrade = async () => {
    setLoading(true);
    try {
      await updateTrade({
        ...trade,
        status: 'rejected'
      });
      onClose();
    } catch (error) {
      console.error('Error rejecting trade:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCancelTrade = async () => {
    setLoading(true);
    try {
      await updateTrade({
        ...trade,
        status: 'cancelled'
      });
      onClose();
    } catch (error) {
      console.error('Error cancelling trade:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCounterOffer = async () => {
    setLoading(true);
    try {
      await updateTrade({
        ...trade,
        status: 'countered',
        receiverCashAmount: counterCash,
        receiverVehicleIds: counterVehicles,
        counterMessage
      });
      onClose();
    } catch (error) {
      console.error('Error sending counter-offer:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSendMessage = async () => {
    if (!messageText.trim()) return;

    setLoading(true);
    try {
      await sendMessage({
        senderId: state.currentUser!.id,
        receiverId: otherUserId,
        tradeId: trade.id,
        content: messageText,
        read: false
      });
      setMessageText('');
    } catch (error) {
      console.error('Error sending message:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleVehicleToggle = (vehicleId: string) => {
    setCounterVehicles(prev => 
      prev.includes(vehicleId) 
        ? prev.filter(id => id !== vehicleId)
        : [...prev, vehicleId]
    );
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'pending': return 'text-yellow-400';
      case 'accepted': return 'text-green-400';
      case 'rejected': return 'text-red-400';
      case 'completed': return 'text-blue-400';
      case 'cancelled': return 'text-gray-400';
      case 'countered': return 'text-purple-400';
      default: return 'text-primary-300';
    }
  };

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50"
      onClick={(e) => e.target === e.currentTarget && onClose()}
    >
      <motion.div
        initial={{ scale: 0.9, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        exit={{ scale: 0.9, opacity: 0 }}
        className="glass-effect rounded-2xl w-full max-w-6xl max-h-[90vh] overflow-y-auto"
      >
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-primary-700/30">
          <div>
            <h1 className="text-2xl font-bold text-primary-100">Trade Details</h1>
            <div className="flex items-center space-x-2 mt-1">
              <span className={`text-sm font-medium capitalize ${getStatusColor(trade.status)}`}>
                {trade.status}
              </span>
              <span className="text-primary-400"></span>
              <span className="text-sm text-primary-400">{formatTimeAgo(trade.updatedAt)}</span>
            </div>
          </div>
          <button
            onClick={onClose}
            className="p-2 hover:bg-primary-800/50 rounded-full transition-colors"
          >
            <X className="w-6 h-6 text-primary-300" />
          </button>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 p-6">
          {/* Left Column - Trade Details */}
          <div className="space-y-6">
            {/* Target Listing */}
            {listing && (
              <div>
                <h3 className="text-lg font-semibold text-primary-100 mb-4">Target Listing</h3>
                <div className="glass-effect rounded-xl p-4">
                  <div className="flex items-center space-x-4">
                    <div className="w-16 h-16 bg-primary-800/30 rounded-lg flex items-center justify-center">
                      {targetVehicle?.images?.[0] ? (
                        <img
                          src={targetVehicle.images[0]}
                          alt={listing.title}
                          className="w-full h-full object-cover rounded-lg"
                        />
                      ) : (
                        <Car className="w-8 h-8 text-primary-400" />
                      )}
                    </div>
                    <div className="flex-1">
                      <h4 className="font-semibold text-primary-100">{listing.title}</h4>
                      <p className="text-sm text-primary-300">
                        {targetVehicle && `${targetVehicle.year} ${targetVehicle.make} ${targetVehicle.model}`}
                      </p>
                      <p className="text-green-400 font-bold">
                        ${listing.price.toLocaleString()}
                      </p>
                    </div>
                  </div>
                </div>
              </div>
            )}

            {/* Offerer's Offer */}
            <div>
              <h3 className="text-lg font-semibold text-primary-100 mb-4">
                {isOutbound ? 'Your Offer' : 'Their Offer'}
              </h3>
              <div className="glass-effect rounded-xl p-4 space-y-4">
                {/* Vehicles */}
                {offererVehicles.length > 0 && (
                  <div>
                    <h4 className="font-medium text-primary-200 mb-2">
                      Vehicles ({offererVehicles.length})
                    </h4>
                    <div className="space-y-2">
                      {offererVehicles.map((vehicle) => (
                        <div key={vehicle?.id} className="flex items-center space-x-3 bg-primary-800/20 rounded-lg p-3">
                          <div className="w-10 h-10 bg-primary-800/30 rounded-lg flex items-center justify-center">
                            {vehicle?.images?.[0] ? (
                              <img
                                src={vehicle.images[0]}
                                alt={`${vehicle.year} ${vehicle.make} ${vehicle.model}`}
                                className="w-full h-full object-cover rounded-lg"
                              />
                            ) : (
                              <Car className="w-5 h-5 text-primary-400" />
                            )}
                          </div>
                          <div className="flex-1">
                            <p className="font-medium text-primary-100">
                              {vehicle?.year} {vehicle?.make} {vehicle?.model}
                            </p>
                            <p className="text-sm text-primary-300">
                              {vehicle?.mileage.toLocaleString()} mi
                            </p>
                          </div>
                          <p className="text-green-400 font-medium">
                            ${(vehicle?.customPrice || vehicle?.estimatedValue || 0).toLocaleString()}
                          </p>
                        </div>
                      ))}
                    </div>
                  </div>
                )}

                {/* Cash */}
                {trade.offererCashAmount !== 0 && (
                  <div className="flex items-center justify-between bg-primary-800/20 rounded-lg p-3">
                    <div className="flex items-center space-x-2">
                      <DollarSign className="w-5 h-5 text-green-400" />
                      <span className="text-primary-200">Cash</span>
                    </div>
                    <span className={`font-bold ${trade.offererCashAmount >= 0 ? 'text-green-400' : 'text-red-400'}`}>
                      {trade.offererCashAmount >= 0 ? '+' : ''}${trade.offererCashAmount.toLocaleString()}
                    </span>
                  </div>
                )}

                {/* Total */}
                <div className="border-t border-primary-700/30 pt-3">
                  <div className="flex items-center justify-between">
                    <span className="font-semibold text-primary-100">Total Value:</span>
                    <span className="text-green-400 font-bold text-lg">
                      ${offererValue.toLocaleString()}
                    </span>
                  </div>
                </div>

                {/* Message */}
                {trade.message && (
                  <div className="border-t border-primary-700/30 pt-3">
                    <p className="text-sm text-primary-300 mb-1">Message:</p>
                    <p className="text-primary-100 italic">"{trade.message}"</p>
                  </div>
                )}
              </div>
            </div>

            {/* Counter Offer (if exists) */}
            {trade.status === 'countered' && (
              <div>
                <h3 className="text-lg font-semibold text-primary-100 mb-4">
                  {isOutbound ? 'Their Counter Offer' : 'Your Counter Offer'}
                </h3>
                <div className="glass-effect rounded-xl p-4 space-y-4 border border-purple-500/30">
                  {/* Counter Vehicles */}
                  {receiverVehicles.length > 0 && (
                    <div>
                      <h4 className="font-medium text-primary-200 mb-2">
                        Vehicles ({receiverVehicles.length})
                      </h4>
                      <div className="space-y-2">
                        {receiverVehicles.map((vehicle) => (
                          <div key={vehicle?.id} className="flex items-center space-x-3 bg-purple-500/10 rounded-lg p-3">
                            <div className="w-10 h-10 bg-primary-800/30 rounded-lg flex items-center justify-center">
                              {vehicle?.images?.[0] ? (
                                <img
                                  src={vehicle.images[0]}
                                  alt={`${vehicle.year} ${vehicle.make} ${vehicle.model}`}
                                  className="w-full h-full object-cover rounded-lg"
                                />
                              ) : (
                                <Car className="w-5 h-5 text-primary-400" />
                              )}
                            </div>
                            <div className="flex-1">
                              <p className="font-medium text-primary-100">
                                {vehicle?.year} {vehicle?.make} {vehicle?.model}
                              </p>
                              <p className="text-sm text-primary-300">
                                {vehicle?.mileage.toLocaleString()} mi
                              </p>
                            </div>
                            <p className="text-purple-400 font-medium">
                              ${(vehicle?.customPrice || vehicle?.estimatedValue || 0).toLocaleString()}
                            </p>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}

                  {/* Counter Cash */}
                  {(trade.receiverCashAmount || 0) !== 0 && (
                    <div className="flex items-center justify-between bg-purple-500/10 rounded-lg p-3">
                      <div className="flex items-center space-x-2">
                        <DollarSign className="w-5 h-5 text-purple-400" />
                        <span className="text-primary-200">Cash</span>
                      </div>
                      <span className={`font-bold ${(trade.receiverCashAmount || 0) >= 0 ? 'text-purple-400' : 'text-red-400'}`}>
                        {(trade.receiverCashAmount || 0) >= 0 ? '+' : ''}${(trade.receiverCashAmount || 0).toLocaleString()}
                      </span>
                    </div>
                  )}

                  {/* Counter Total */}
                  <div className="border-t border-purple-500/30 pt-3">
                    <div className="flex items-center justify-between">
                      <span className="font-semibold text-primary-100">Counter Value:</span>
                      <span className="text-purple-400 font-bold text-lg">
                        ${receiverValue.toLocaleString()}
                      </span>
                    </div>
                  </div>

                  {/* Counter Message */}
                  {trade.counterMessage && (
                    <div className="border-t border-purple-500/30 pt-3">
                      <p className="text-sm text-primary-300 mb-1">Counter Message:</p>
                      <p className="text-primary-100 italic">"{trade.counterMessage}"</p>
                    </div>
                  )}
                </div>
              </div>
            )}

            {/* Trade History */}
            {trade.history && trade.history.length > 0 && (
              <div>
                <h3 className="text-lg font-semibold text-primary-100 mb-4">Trade History</h3>
                <div className="glass-effect rounded-xl p-4">
                  <div className="space-y-3">
                    {trade.history.map((item, index) => (
                      <div key={index} className="flex items-center space-x-3 text-sm">
                        <div className={`w-2 h-2 rounded-full ${getStatusColor(item.action)}`} />
                        <span className="text-primary-300">{formatTimeAgo(item.timestamp)}</span>
                        <span className="text-primary-100 capitalize">{item.action}</span>
                        {item.note && <span className="text-primary-400">- {item.note}</span>}
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            )}
          </div>

          {/* Right Column - Actions & Messages */}
          <div className="space-y-6">
            {/* Trade Actions */}
            {trade.status === 'pending' && (
              <div>
                <h3 className="text-lg font-semibold text-primary-100 mb-4">Actions</h3>
                <div className="glass-effect rounded-xl p-4 space-y-3">
                  {!isOutbound ? (
                    <>
                      <button
                        onClick={handleAcceptTrade}
                        className="w-full bg-green-600 hover:bg-green-700 text-white font-medium py-3 px-4 rounded-lg transition-colors flex items-center justify-center space-x-2"
                      >
                        <Check className="w-5 h-5" />
                        <span>Accept Trade</span>
                      </button>
                      <button
                        onClick={() => setShowCounterModal(true)}
                        className="w-full bg-purple-600 hover:bg-purple-700 text-white font-medium py-3 px-4 rounded-lg transition-colors flex items-center justify-center space-x-2"
                      >
                        <ArrowLeftRight className="w-5 h-5" />
                        <span>Counter Offer</span>
                      </button>
                      <button
                        onClick={handleRejectTrade}
                        className="w-full bg-red-600 hover:bg-red-700 text-white font-medium py-3 px-4 rounded-lg transition-colors flex items-center justify-center space-x-2"
                      >
                        <X className="w-5 h-5" />
                        <span>Reject Trade</span>
                      </button>
                    </>
                  ) : (
                    <button
                      onClick={handleCancelTrade}
                      className="w-full bg-red-600 hover:bg-red-700 text-white font-medium py-3 px-4 rounded-lg transition-colors flex items-center justify-center space-x-2"
                    >
                      <X className="w-5 h-5" />
                      <span>Cancel Trade</span>
                    </button>
                  )}
                </div>
              </div>
            )}

            {/* Counter Offer Actions */}
            {trade.status === 'countered' && (
              <div>
                <h3 className="text-lg font-semibold text-primary-100 mb-4">Counter Offer Actions</h3>
                <div className="glass-effect rounded-xl p-4 space-y-3">
                  {isOutbound ? (
                    <>
                      <button
                        onClick={handleAcceptTrade}
                        className="w-full bg-green-600 hover:bg-green-700 text-white font-medium py-3 px-4 rounded-lg transition-colors flex items-center justify-center space-x-2"
                      >
                        <Check className="w-5 h-5" />
                        <span>Accept Counter</span>
                      </button>
                      <button
                        onClick={() => setShowCounterModal(true)}
                        className="w-full bg-purple-600 hover:bg-purple-700 text-white font-medium py-3 px-4 rounded-lg transition-colors flex items-center justify-center space-x-2"
                      >
                        <ArrowLeftRight className="w-5 h-5" />
                        <span>New Counter</span>
                      </button>
                      <button
                        onClick={handleRejectTrade}
                        className="w-full bg-red-600 hover:bg-red-700 text-white font-medium py-3 px-4 rounded-lg transition-colors flex items-center justify-center space-x-2"
                      >
                        <X className="w-5 h-5" />
                        <span>Reject Counter</span>
                      </button>
                    </>
                  ) : (
                    <div className="text-center text-primary-300">
                      <p>Waiting for their response to your counter offer...</p>
                    </div>
                  )}
                </div>
              </div>
            )}

            {/* Quick Message */}
            <div>
              <h3 className="text-lg font-semibold text-primary-100 mb-4">Send Message</h3>
              <div className="glass-effect rounded-xl p-4 space-y-3">
                <textarea
                  value={newMessage}
                  onChange={(e) => setNewMessage(e.target.value)}
                  placeholder="Send a message about this trade..."
                  className="w-full bg-primary-800/30 border border-primary-700/50 rounded-lg px-3 py-2 text-primary-100 placeholder-primary-400 focus:outline-none focus:border-primary-500 resize-none"
                  rows={3}
                />
                <button
                  onClick={handleSendMessage}
                  disabled={!newMessage.trim()}
                  className="w-full bg-primary-600 hover:bg-primary-700 disabled:opacity-50 disabled:cursor-not-allowed text-white font-medium py-2 px-4 rounded-lg transition-colors flex items-center justify-center space-x-2"
                >
                  <MessageCircle className="w-4 h-4" />
                  <span>Send Message</span>
                </button>
              </div>
            </div>

            {/* Trade Summary */}
            <div>
              <h3 className="text-lg font-semibold text-primary-100 mb-4">Trade Summary</h3>
              <div className="glass-effect rounded-xl p-4 space-y-3">
                <div className="flex items-center justify-between">
                  <span className="text-primary-300">Your Offer Value:</span>
                  <span className="text-green-400 font-bold">
                    ${isOutbound ? offererValue.toLocaleString() : receiverValue.toLocaleString()}
                  </span>
                </div>
                <div className="flex items-center justify-between">
                  <span className="text-primary-300">Their Offer Value:</span>
                  <span className="text-purple-400 font-bold">
                    ${isOutbound ? receiverValue.toLocaleString() : offererValue.toLocaleString()}
                  </span>
                </div>
                <div className="border-t border-primary-700/30 pt-3">
                  <div className="flex items-center justify-between">
                    <span className="text-primary-300">Difference:</span>
                    <span className={`font-bold ${Math.abs(offererValue - receiverValue) < 1000 ? 'text-green-400' : 'text-yellow-400'}`}>
                      ${Math.abs(offererValue - receiverValue).toLocaleString()}
                    </span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Counter Offer Modal */}
        {showCounterModal && (
          <div className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50">
            <div className="glass-effect rounded-2xl w-full max-w-2xl max-h-[80vh] overflow-y-auto">
              <div className="p-6">
                <h2 className="text-xl font-bold text-primary-100 mb-4">Make Counter Offer</h2>
                
                {/* Vehicle Selection */}
                <div className="mb-6">
                  <h3 className="font-semibold text-primary-200 mb-3">Select Your Vehicles</h3>
                  <div className="space-y-2 max-h-60 overflow-y-auto">
                    {userVehicles.map((vehicle) => (
                      <div
                        key={vehicle.id}
                        className={`flex items-center space-x-3 p-3 rounded-lg cursor-pointer transition-colors ${
                          counterVehicleIds.includes(vehicle.id)
                            ? 'bg-primary-600/30 border border-primary-500'
                            : 'bg-primary-800/20 hover:bg-primary-800/40'
                        }`}
                        onClick={() => handleVehicleToggle(vehicle.id)}
                      >
                        <div className="w-12 h-12 bg-primary-800/30 rounded-lg flex items-center justify-center">
                          {vehicle.images?.[0] ? (
                            <img
                              src={vehicle.images[0]}
                              alt={`${vehicle.year} ${vehicle.make} ${vehicle.model}`}
                              className="w-full h-full object-cover rounded-lg"
                            />
                          ) : (
                            <Car className="w-6 h-6 text-primary-400" />
                          )}
                        </div>
                        <div className="flex-1">
                          <p className="font-medium text-primary-100">
                            {vehicle.year} {vehicle.make} {vehicle.model}
                          </p>
                          <p className="text-sm text-primary-300">
                            {vehicle.mileage.toLocaleString()} mi
                          </p>
                        </div>
                        <p className="text-green-400 font-medium">
                          ${(vehicle.customPrice || vehicle.estimatedValue).toLocaleString()}
                        </p>
                        <div className={`w-5 h-5 rounded border-2 flex items-center justify-center ${
                          counterVehicleIds.includes(vehicle.id)
                            ? 'bg-primary-500 border-primary-500'
                            : 'border-primary-600'
                        }`}>
                          {counterVehicleIds.includes(vehicle.id) && (
                            <Check className="w-3 h-3 text-white" />
                          )}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                {/* Cash Amount */}
                <div className="mb-6">
                  <label className="block text-sm font-medium text-primary-200 mb-2">
                    Cash Amount (+ to add, - to request)
                  </label>
                  <input
                    type="number"
                    value={counterCashAmount === 0 ? '' : counterCashAmount}
                    onChange={(e) => setCounterCashAmount(e.target.value === '' ? 0 : parseInt(e.target.value) || 0)}
                    placeholder="0"
                    className="w-full bg-primary-800/30 border border-primary-700/50 rounded-lg px-3 py-2 text-primary-100 placeholder-primary-400 focus:outline-none focus:border-primary-500"
                  />
                </div>

                {/* Counter Message */}
                <div className="mb-6">
                  <label className="block text-sm font-medium text-primary-200 mb-2">
                    Message (optional)
                  </label>
                  <textarea
                    value={counterMessage}
                    onChange={(e) => setCounterMessage(e.target.value)}
                    placeholder="Explain your counter offer..."
                    className="w-full bg-primary-800/30 border border-primary-700/50 rounded-lg px-3 py-2 text-primary-100 placeholder-primary-400 focus:outline-none focus:border-primary-500 resize-none"
                    rows={3}
                  />
                </div>

                {/* Counter Value Display */}
                <div className="mb-6 p-4 bg-purple-500/10 rounded-lg border border-purple-500/30">
                  <div className="flex items-center justify-between">
                    <span className="text-primary-200">Counter Offer Value:</span>
                    <span className="text-purple-400 font-bold text-lg">
                      ${(counterVehicleIds.reduce((sum, id) => {
                        const vehicle = userVehicles.find(v => v.id === id);
                        return sum + (vehicle?.customPrice || vehicle?.estimatedValue || 0);
                      }, 0) + counterCashAmount).toLocaleString()}
                    </span>
                  </div>
                </div>

                {/* Actions */}
                <div className="flex space-x-3">
                  <button
                    onClick={() => setShowCounterModal(false)}
                    className="flex-1 bg-primary-700 hover:bg-primary-600 text-primary-100 font-medium py-2 px-4 rounded-lg transition-colors"
                  >
                    Cancel
                  </button>
                  <button
                    onClick={handleCounterOffer}
                    disabled={counterVehicleIds.length === 0}
                    className="flex-1 bg-purple-600 hover:bg-purple-700 disabled:opacity-50 disabled:cursor-not-allowed text-white font-medium py-2 px-4 rounded-lg transition-colors"
                  >
                    Send Counter
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}
      </motion.div>
    </motion.div>
  );
};
