import React, { useState, useMemo, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  ArrowLeftRight, 
  Car, 
  DollarSign, 
  Clock, 
  CheckCircle, 
  XCircle, 
  MessageCircle,
  User,
  Eye,
  Calendar,
  Filter,
  Search,
  RefreshCw
} from 'lucide-react';
import { useApp } from '../../context/AppContext.tsx';
import { Trade, Listing } from '../../types/index.ts';
import { TradeDetailModal } from './TradeDetailModal.tsx';

export function TradesView() {
  const { state, loadAllListings } = useApp();
  const [activeTab, setActiveTab] = useState<'outbound' | 'inbound'>('outbound');
  const [selectedTrade, setSelectedTrade] = useState<Trade | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState<string>('all');
  const [showFilters, setShowFilters] = useState(false);

  // Load listings when component mounts to ensure we have listing data
  useEffect(() => {
    if (state.allListings.length === 0) {
      loadAllListings();
    }
  }, [state.allListings.length, loadAllListings]);

  // Separate outbound and inbound trades
  const outboundTrades = useMemo(() => {
    const filtered = state.trades.filter(trade => {
      // Handle both string IDs and populated user objects
      const offererId = typeof trade.offererUserId === 'string' 
        ? trade.offererUserId 
        : (trade.offererUserId as any)?._id || (trade.offererUserId as any)?.id;
      
      return offererId === state.currentUser?.id;
    });
    
    return filtered;
  }, [state.trades, state.currentUser?.id]);

  const inboundTrades = useMemo(() => {
    const filtered = state.trades.filter(trade => {
      // Handle both string IDs and populated user objects
      const receiverId = typeof trade.receiverUserId === 'string' 
        ? trade.receiverUserId 
        : (trade.receiverUserId as any)?._id || (trade.receiverUserId as any)?.id;
      
      return receiverId === state.currentUser?.id;
    });
    
    return filtered;
  }, [state.trades, state.currentUser?.id]);

  const currentTrades = activeTab === 'outbound' ? outboundTrades : inboundTrades;

  // Filter trades based on search and status
  const filteredTrades = useMemo(() => {
    return currentTrades.filter(trade => {
      // Handle populated listing objects
      const listingId = typeof trade.listingId === 'string' 
        ? trade.listingId 
        : (trade.listingId as any)?._id || (trade.listingId as any)?.id;
      
      const listing = state.allListings.find(l => l.id === listingId);
      
      // Handle populated user objects
      const otherUserId = activeTab === 'outbound' 
        ? (typeof trade.receiverUserId === 'string' ? trade.receiverUserId : (trade.receiverUserId as any)?._id || (trade.receiverUserId as any)?.id)
        : (typeof trade.offererUserId === 'string' ? trade.offererUserId : (trade.offererUserId as any)?._id || (trade.offererUserId as any)?.id);
      
      const otherUser = state.users.find(u => u.id === otherUserId);
      
      const matchesSearch = !searchTerm || 
        listing?.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        otherUser?.username.toLowerCase().includes(searchTerm.toLowerCase());
      
      const matchesStatus = statusFilter === 'all' || trade.status === statusFilter;
      
      return matchesSearch && matchesStatus;
    });
  }, [currentTrades, searchTerm, statusFilter, state.allListings, state.users, activeTab]);

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'pending': return 'text-yellow-400';
      case 'accepted': return 'text-green-400';
      case 'rejected': return 'text-red-400';
      case 'completed': return 'text-blue-400';
      case 'cancelled': return 'text-gray-400';
      case 'countered': return 'text-purple-400';
      default: return 'text-primary-300';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'pending': return <Clock className="w-4 h-4" />;
      case 'accepted': return <CheckCircle className="w-4 h-4" />;
      case 'rejected': return <XCircle className="w-4 h-4" />;
      case 'completed': return <CheckCircle className="w-4 h-4" />;
      case 'cancelled': return <XCircle className="w-4 h-4" />;
      case 'countered': return <ArrowLeftRight className="w-4 h-4" />;
      default: return <Clock className="w-4 h-4" />;
    }
  };

  const formatTimeAgo = (dateString: string) => {
    try {
      const now = new Date();
      const date = new Date(dateString);
      const diffInMinutes = Math.floor((now.getTime() - date.getTime()) / (1000 * 60));
      
      if (diffInMinutes < 1) return 'Just now';
      if (diffInMinutes < 60) return `${diffInMinutes}m ago`;
      
      const diffInHours = Math.floor(diffInMinutes / 60);
      if (diffInHours < 24) return `${diffInHours}h ago`;
      
      const diffInDays = Math.floor(diffInHours / 24);
      if (diffInDays < 7) return `${diffInDays}d ago`;
      if (diffInDays < 30) return `${Math.floor(diffInDays / 7)}w ago`;
      
      return date.toLocaleDateString();
    } catch (error) {
      return 'Unknown';
    }
  };

  const handleTradeClick = (trade: Trade) => {
    setSelectedTrade(trade);
  };

  const renderTradeCard = (trade: Trade, isOutbound: boolean) => {
    const listing = state.allListings.find(l => l.id === trade.listingId);
    
    if (!listing) {
      return (
        <div key={trade.id} className="glass-effect rounded-xl p-4 border border-red-500/30">
          <p className="text-red-400">Listing not found</p>
        </div>
      );
    }

    // Extract user IDs properly (handle both string IDs and populated objects)
    const otherUserId = isOutbound 
      ? (typeof trade.receiverUserId === 'string' ? trade.receiverUserId : (trade.receiverUserId as any)?._id || (trade.receiverUserId as any)?.id)
      : (typeof trade.offererUserId === 'string' ? trade.offererUserId : (trade.offererUserId as any)?._id || (trade.offererUserId as any)?.id);
    
    // Get other user info
    const otherUser = state.users.find(u => u.id === otherUserId) || {
      id: otherUserId,
      username: 'Unknown User',
      email: '',
      password: '',
      createdAt: '',
      location: ''
    };

    // Get vehicles involved in the trade
    const offererVehicles = trade.offererVehicleIds.map(vId => 
      state.vehicles.find(v => v.id === vId)
    ).filter(Boolean);

    const receiverVehicles = (trade.receiverVehicleIds || []).map(vId => 
      state.vehicles.find(v => v.id === vId)
    ).filter(Boolean);

    // Calculate values
    const totalOffererValue = trade.offererCashAmount +
      offererVehicles.reduce((sum, v) => sum + (v?.customPrice || v?.estimatedValue || 0), 0);

    const totalReceiverValue = (trade.receiverCashAmount || 0) +
      receiverVehicles.reduce((sum, v) => sum + (v?.customPrice || v?.estimatedValue || 0), 0);

    // Get vehicles to display based on trade direction
    const tradedVehicles = isOutbound ? offererVehicles : receiverVehicles;
    const vehicleCount = isOutbound ? trade.offererVehicleIds.length : (trade.receiverVehicleIds || []).length;

    return (
      <motion.div
        key={trade.id}
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="glass-effect rounded-xl p-6 hover:bg-primary-800/20 transition-all cursor-pointer"
        onClick={() => setSelectedTrade(trade)}
      >
        <div className="flex space-x-4">
          {/* Listing Image */}
          <div className="w-20 h-20 bg-primary-800/30 rounded-lg flex items-center justify-center flex-shrink-0">
            {listing.vehicle?.images?.[0] ? (
              <img
                src={listing.vehicle.images[0]}
                alt={listing.title}
                className="w-full h-full object-cover rounded-lg"
              />
            ) : (
              <Car className="w-8 h-8 text-primary-400" />
            )}
          </div>

          <div className="flex-1">
            {/* Header */}
            <div className="flex items-center justify-between mb-2">
              <h3 className="font-semibold text-primary-100">{listing.title}</h3>
              <div className={`flex items-center space-x-1 ${getStatusColor(trade.status)}`}>
                {getStatusIcon(trade.status)}
                <span className="text-sm font-medium capitalize">{trade.status}</span>
              </div>
            </div>

            {/* Trade Summary */}
            <div className="space-y-2 mb-3">
              <div className="flex items-center justify-between text-sm">
                <span className="text-primary-300">
                  {isOutbound ? 'Your Offer:' : 'Their Offer:'}
                </span>
                <span className="text-green-400 font-medium">
                  ${totalOffererValue.toLocaleString()}
                </span>
              </div>
              
              {trade.status === 'countered' && (
                <div className="flex items-center justify-between text-sm">
                  <span className="text-primary-300">Counter Offer:</span>
                  <span className="text-purple-400 font-medium">
                    ${totalReceiverValue.toLocaleString()}
                  </span>
                </div>
              )}

              {/* Show vehicles in the trade */}
              {vehicleCount > 0 && (
                <div className="text-sm text-primary-300">
                  <span>{vehicleCount} vehicle{vehicleCount !== 1 ? 's' : ''} included</span>
                  {tradedVehicles.length > 0 && (
                    <div className="mt-1 text-xs">
                      {tradedVehicles.slice(0, 2).map((vehicle, index) => (
                        <span key={vehicle?.id} className="text-primary-400">
                          {vehicle?.year} {vehicle?.make} {vehicle?.model}
                          {index < Math.min(tradedVehicles.length, 2) - 1 && ', '}
                        </span>
                      ))}
                      {tradedVehicles.length > 2 && (
                        <span className="text-primary-400"> +{tradedVehicles.length - 2} more</span>
                      )}
                    </div>
                  )}
                </div>
              )}
            </div>

            {/* Other User Info */}
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-2">
                <div className="w-6 h-6 bg-primary-800/50 rounded-full flex items-center justify-center">
                  <User className="w-4 h-4 text-primary-300" />
                </div>
                <span className="text-sm text-primary-300">
                  {isOutbound ? 'To:' : 'From:'} @{otherUser.username}
                </span>
              </div>
              <span className="text-xs text-primary-400">
                {formatTimeAgo(trade.updatedAt)}
              </span>
            </div>
          </div>
        </div>
      </motion.div>
    );
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-primary-100">Trades</h1>
          <p className="text-primary-300 mt-1">
            Manage your vehicle trade offers and negotiations
          </p>
        </div>
      </div>

      {/* Tabs */}
      <div className="flex space-x-1 bg-primary-800/30 rounded-lg p-1">
        <button
          onClick={() => setActiveTab('outbound')}
          className={`flex-1 py-2 px-4 rounded-md text-sm font-medium transition-colors ${
            activeTab === 'outbound'
              ? 'bg-primary-100/20 text-primary-100'
              : 'text-primary-300 hover:text-primary-200'
          }`}
        >
          Outbound ({outboundTrades.length})
        </button>
        <button
          onClick={() => setActiveTab('inbound')}
          className={`flex-1 py-2 px-4 rounded-md text-sm font-medium transition-colors ${
            activeTab === 'inbound'
              ? 'bg-primary-100/20 text-primary-100'
              : 'text-primary-300 hover:text-primary-200'
          }`}
        >
          Inbound ({inboundTrades.length})
        </button>
      </div>

      {/* Search and Filters */}
      <div className="glass-effect rounded-xl p-6">
        <div className="flex flex-col lg:flex-row gap-4">
          {/* Search Bar */}
          <div className="flex-1 relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-primary-400" />
            <input
              type="text"
              placeholder="Search by listing title or username..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="input-field pl-10"
            />
          </div>

          {/* Filter Toggle */}
          <motion.button
            whileTap={{ scale: 0.95 }}
            onClick={() => setShowFilters(!showFilters)}
            className={`btn-secondary flex items-center space-x-2 ${showFilters ? 'bg-primary-100/20' : ''}`}
          >
            <Filter className="w-5 h-5" />
            <span>Filters</span>
          </motion.button>
        </div>

        {/* Expanded Filters */}
        {showFilters && (
          <motion.div
            initial={{ opacity: 0, height: 0 }}
            animate={{ opacity: 1, height: 'auto' }}
            exit={{ opacity: 0, height: 0 }}
            className="mt-4 pt-4 border-t border-primary-700/30"
          >
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-primary-300 mb-2">
                  Status
                </label>
                <select
                  value={statusFilter}
                  onChange={(e) => setStatusFilter(e.target.value)}
                  className="input-field"
                >
                  <option value="all">All Statuses</option>
                  <option value="pending">Pending</option>
                  <option value="countered">Countered</option>
                  <option value="accepted">Accepted</option>
                  <option value="rejected">Rejected</option>
                  <option value="completed">Completed</option>
                  <option value="cancelled">Cancelled</option>
                </select>
              </div>
            </div>
          </motion.div>
        )}
      </div>

      {/* Trades List */}
      <div className="space-y-4">
        {filteredTrades.length === 0 ? (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className="text-center py-16"
          >
            <div className="w-24 h-24 bg-primary-800/50 rounded-full flex items-center justify-center mx-auto mb-4">
              <ArrowLeftRight className="w-12 h-12 text-primary-400" />
            </div>
            <h3 className="text-xl font-semibold text-primary-200 mb-2">
              No {activeTab} trades found
            </h3>
            <p className="text-primary-400 mb-6">
              {activeTab === 'outbound' 
                ? "You haven't made any trade offers yet. Go to Listings to make trade offers on vehicles you're interested in."
                : "You haven't received any trade offers yet"
              }
            </p>
          </motion.div>
        ) : (
          <div className="space-y-4">
            <AnimatePresence>
              {filteredTrades.map((trade) => renderTradeCard(trade, activeTab === 'outbound'))}
            </AnimatePresence>
          </div>
        )}
      </div>

      {/* Trade Detail Modal */}
      <AnimatePresence>
        {selectedTrade && (
          <TradeDetailModal
            trade={selectedTrade}
            isOutbound={activeTab === 'outbound'}
            onClose={() => setSelectedTrade(null)}
          />
        )}
      </AnimatePresence>
    </div>
  );
}
