import React, { useState, useEffect, useRef, useMemo, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  MessageCircle, 
  Search, 
  Send, 
  User, 
  ArrowLeft, 
  MoreVertical,
  Check,
  CheckCheck,
  Phone,
  Video,
  Info
} from 'lucide-react';
import { useApp } from '../../context/AppContext.tsx';
import { Message, User as UserType, Conversation } from '../../types/index.ts';

interface TypingIndicator {
  userId: string;
  conversationId: string;
  timestamp: number;
}

// Virtual scrolling constants
const MESSAGE_HEIGHT = 80; // Approximate message height
const OVERSCAN = 5; // Number of items to render outside visible area

export function MessagesView() {
  const { state, sendMessage, markMessagesAsRead } = useApp();
  const [selectedConversation, setSelectedConversation] = useState<string | null>(null);
  const [messageText, setMessageText] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(false);
  const [typingUsers, setTypingUsers] = useState<TypingIndicator[]>([]);
  const [isTyping, setIsTyping] = useState(false);
  const [scrollTop, setScrollTop] = useState(0);
  const [containerHeight, setContainerHeight] = useState(0);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const messagesContainerRef = useRef<HTMLDivElement>(null);
  const typingTimeoutRef = useRef<NodeJS.Timeout>();

  // Debug logging
  useEffect(() => {
    console.log('ðŸ” MessagesView Debug:', {
      messagesCount: state.messages.length,
      usersCount: state.users.length,
      currentUser: state.currentUser?.username,
      messages: state.messages.slice(0, 3).map(m => ({
        id: m.id,
        senderId: m.senderId,
        receiverId: m.receiverId,
        content: m.content.substring(0, 30) + '...'
      })),
      users: state.users.slice(0, 5).map(u => ({ id: u.id, username: u.username }))
    });
  }, [state.messages, state.users, state.currentUser]);

  // Handle scroll for virtual scrolling
  const handleScroll = useCallback((e: React.UIEvent<HTMLDivElement>) => {
    const target = e.target as HTMLDivElement;
    setScrollTop(target.scrollTop);
  }, []);

  // Measure container height
  useEffect(() => {
    const updateHeight = () => {
      if (messagesContainerRef.current) {
        setContainerHeight(messagesContainerRef.current.clientHeight);
      }
    };

    updateHeight();
    window.addEventListener('resize', updateHeight);
    return () => window.removeEventListener('resize', updateHeight);
  }, []);

  // Auto-scroll to bottom when new messages arrive
  useEffect(() => {
    // Use setTimeout to ensure DOM has been updated before scrolling
    const timeoutId = setTimeout(() => {
      scrollToBottom();
    }, 50); // Reduced delay for smoother experience

    return () => clearTimeout(timeoutId);
  }, [state.messages, selectedConversation]);

  // Scroll to bottom when conversation is first selected
  useEffect(() => {
    if (selectedConversation) {
      const timeoutId = setTimeout(() => {
        scrollToBottom();
      }, 100);

      return () => clearTimeout(timeoutId);
    }
  }, [selectedConversation]);

  // Clean up typing indicators that are too old
  useEffect(() => {
    const interval = setInterval(() => {
      const now = Date.now();
      setTypingUsers(prev => prev.filter(typing => now - typing.timestamp < 3000));
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  const scrollToBottom = () => {
    if (messagesContainerRef.current) {
      messagesContainerRef.current.scrollTop = messagesContainerRef.current.scrollHeight;
    }
  };

  // Create conversations from messages
  const conversations = useMemo(() => {
    const convMap = new Map<string, Conversation>();
    
    state.messages.forEach(message => {
      // Handle both string IDs and populated user objects
      const messageSenderId = typeof message.senderId === 'string' 
        ? message.senderId 
        : (message.senderId as any)?.id || (message.senderId as any)?._id;
      const messageReceiverId = typeof message.receiverId === 'string' 
        ? message.receiverId 
        : (message.receiverId as any)?.id || (message.receiverId as any)?._id;

      const otherUserId = messageSenderId === state.currentUser?.id 
        ? messageReceiverId 
        : messageSenderId;
      
      const conversationId = [state.currentUser?.id, otherUserId].sort().join('-');
      
      if (!convMap.has(conversationId)) {
        convMap.set(conversationId, {
          id: conversationId,
          participants: [state.currentUser?.id!, otherUserId],
          lastMessage: message,
          unreadCount: 0,
          updatedAt: message.timestamp
        });
      } else {
        const existing = convMap.get(conversationId)!;
        if (new Date(message.timestamp) > new Date(existing.lastMessage.timestamp)) {
          existing.lastMessage = message;
          existing.updatedAt = message.timestamp;
        }
      }
    });

    // Calculate unread counts
    convMap.forEach(conversation => {
      const unreadMessages = state.messages.filter(msg => {
        // Handle populated user objects in unread count calculation too
        const msgReceiverId = typeof msg.receiverId === 'string' 
          ? msg.receiverId 
          : (msg.receiverId as any)?.id || (msg.receiverId as any)?._id;
        const msgSenderId = typeof msg.senderId === 'string' 
          ? msg.senderId 
          : (msg.senderId as any)?.id || (msg.senderId as any)?._id;

        return msgReceiverId === state.currentUser?.id &&
          !msg.read &&
          (msgSenderId === conversation.participants[0] || msgSenderId === conversation.participants[1]);
      });
      conversation.unreadCount = unreadMessages.length;
    });

    const conversationsArray = Array.from(convMap.values()).sort((a, b) => 
      new Date(b.updatedAt).getTime() - new Date(a.updatedAt).getTime()
    );

    // Debug: Check for missing users
    const missingUsers = conversationsArray.map(conv => {
      const otherUserId = conv.participants.find(p => p !== state.currentUser?.id);
      const otherUser = state.users.find(u => u.id === otherUserId);
      return { otherUserId, found: !!otherUser, username: otherUser?.username };
    });

    console.log('ðŸ” Conversations Debug:', {
      conversationsCount: conversationsArray.length,
      missingUsers: missingUsers.filter(u => !u.found),
      allUserMappings: missingUsers
    });

    return conversationsArray;
  }, [state.messages, state.currentUser?.id, state.users]);

  // Filter conversations based on search
  const filteredConversations = useMemo(() => {
    if (!searchTerm) return conversations;
    
    return conversations.filter(conversation => {
      const otherUserId = conversation.participants.find(p => p !== state.currentUser?.id);
      const otherUser = state.users.find(u => u.id === otherUserId);
      return otherUser?.username.toLowerCase().includes(searchTerm.toLowerCase());
    });
  }, [conversations, searchTerm, state.users, state.currentUser?.id]);

  // Get messages for selected conversation
  const conversationMessages = useMemo(() => {
    if (!selectedConversation) return [];
    
    const conversation = conversations.find(c => c.id === selectedConversation);
    if (!conversation) return [];
    
    return state.messages
      .filter(msg => {
        // Handle populated user objects
        const msgSenderId = typeof msg.senderId === 'string' 
          ? msg.senderId 
          : (msg.senderId as any)?.id || (msg.senderId as any)?._id;
        const msgReceiverId = typeof msg.receiverId === 'string' 
          ? msg.receiverId 
          : (msg.receiverId as any)?.id || (msg.receiverId as any)?._id;

        const participants = conversation.participants;
        return (
          (msgSenderId === participants[0] && msgReceiverId === participants[1]) ||
          (msgSenderId === participants[1] && msgReceiverId === participants[0])
        );
      })
      .sort((a, b) => new Date(a.timestamp).getTime() - new Date(b.timestamp).getTime());
  }, [selectedConversation, conversations, state.messages]);

  // Get other user in selected conversation
  const selectedConversationUser = useMemo(() => {
    if (!selectedConversation) return null;
    
    const conversation = conversations.find(c => c.id === selectedConversation);
    if (!conversation) return null;
    
    const otherUserId = conversation.participants.find(p => p !== state.currentUser?.id);
    return state.users.find(u => u.id === otherUserId) || null;
  }, [selectedConversation, conversations, state.users, state.currentUser?.id]);

  const handleSendMessage = async () => {
    if (!messageText.trim() || !selectedConversationUser || loading) return;

    setLoading(true);
    try {
      await sendMessage({
        senderId: state.currentUser!.id,
        receiverId: selectedConversationUser.id,
        content: messageText.trim()
      });
      setMessageText('');
      setIsTyping(false);
      
      // Scroll to bottom after sending message
      setTimeout(() => {
        scrollToBottom();
      }, 100);
    } catch (error) {
      console.error('Error sending message:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleTyping = (value: string) => {
    setMessageText(value);
    
    if (value.trim() && !isTyping) {
      setIsTyping(true);
      // In a real app, you'd emit typing indicator to other users via WebSocket
    }

    // Clear typing indicator after 2 seconds of no typing
    if (typingTimeoutRef.current) {
      clearTimeout(typingTimeoutRef.current);
    }
    
    typingTimeoutRef.current = setTimeout(() => {
      setIsTyping(false);
    }, 2000);
  };

  const handleConversationSelect = async (conversationId: string) => {
    setSelectedConversation(conversationId);
    
    // Mark messages as read
    const conversation = conversations.find(c => c.id === conversationId);
    if (conversation && conversation.unreadCount > 0) {
      try {
        await markMessagesAsRead(conversationId);
      } catch (error) {
        console.error('Error marking messages as read:', error);
      }
    }
  };

  const formatTime = (timestamp: string) => {
    const date = new Date(timestamp);
    const now = new Date();
    const diffInHours = (now.getTime() - date.getTime()) / (1000 * 60 * 60);
    
    if (diffInHours < 24) {
      return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
    } else if (diffInHours < 168) { // 7 days
      return date.toLocaleDateString([], { weekday: 'short' });
    } else {
      return date.toLocaleDateString([], { month: 'short', day: 'numeric' });
    }
  };

  const formatMessageTime = (timestamp: string) => {
    return new Date(timestamp).toLocaleTimeString([], { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  const renderConversationsList = () => (
    <div className="w-full lg:w-96 border-r border-primary-700/30 flex flex-col">
      {/* Header */}
      <div className="p-4 border-b border-primary-700/30">
        <h2 className="text-xl font-bold text-primary-100 mb-4">Messages</h2>
        
        {/* Search */}
        <div className="relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-primary-400" />
          <input
            type="text"
            placeholder="Search conversations..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="input-field pl-10 text-sm"
          />
        </div>
      </div>

      {/* Conversations List */}
      <div className="flex-1 overflow-y-auto">
        {filteredConversations.length === 0 ? (
          <div className="p-4 text-center">
            <MessageCircle className="w-12 h-12 mx-auto mb-4 text-primary-400" />
            <p className="text-primary-300">No conversations yet</p>
            <p className="text-sm text-primary-400 mt-1">
              Start trading to begin messaging with other users
            </p>
          </div>
        ) : (
          <div className="space-y-1 p-2">
            {filteredConversations.map((conversation) => {
              const otherUserId = conversation.participants.find(p => p !== state.currentUser?.id);
              const otherUser = state.users.find(u => u.id === otherUserId);
              const isSelected = selectedConversation === conversation.id;
              
              return (
                <motion.div
                  key={conversation.id}
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                  onClick={() => handleConversationSelect(conversation.id)}
                  className={`p-3 rounded-lg cursor-pointer transition-all ${
                    isSelected
                      ? 'bg-primary-600/30 border border-primary-500/50'
                      : 'hover:bg-primary-800/30'
                  }`}
                >
                  <div className="flex items-center space-x-3">
                    {/* Avatar */}
                    <div className="relative">
                      <div className="w-12 h-12 bg-primary-800/50 rounded-full flex items-center justify-center">
                        {otherUser?.avatar ? (
                          <img
                            src={otherUser.avatar}
                            alt={otherUser.username}
                            className="w-full h-full object-cover rounded-full"
                          />
                        ) : (
                          <User className="w-6 h-6 text-primary-300" />
                        )}
                      </div>
                      {/* Online indicator could go here */}
                    </div>
                    
                    {/* Content */}
                    <div className="flex-1 min-w-0">
                      <div className="flex items-center justify-between">
                        <h4 className="font-medium text-primary-100 truncate">
                          @{otherUser?.username || 'Unknown'}
                        </h4>
                        <span className="text-xs text-primary-400">
                          {formatTime(conversation.lastMessage.timestamp)}
                        </span>
                      </div>
                      
                      <div className="flex items-center justify-between mt-1">
                        <p className="text-sm text-primary-300 truncate">
                          {(() => {
                            // Handle populated user objects for last message sender
                            const lastMessageSenderId = typeof conversation.lastMessage.senderId === 'string' 
                              ? conversation.lastMessage.senderId 
                              : (conversation.lastMessage.senderId as any)?.id || (conversation.lastMessage.senderId as any)?._id;
                            return lastMessageSenderId === state.currentUser?.id ? 'You: ' : '';
                          })()}
                          {conversation.lastMessage.content}
                        </p>
                        {conversation.unreadCount > 0 && (
                          <span className="bg-blue-500 text-white text-xs px-2 py-1 rounded-full min-w-[20px] text-center">
                            {conversation.unreadCount > 99 ? '99+' : conversation.unreadCount}
                          </span>
                        )}
                      </div>
                    </div>
                  </div>
                </motion.div>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );

  const renderChatInterface = () => {
    if (!selectedConversation || !selectedConversationUser) {
      return (
        <div className="flex-1 flex items-center justify-center">
          <div className="text-center">
            <MessageCircle className="w-16 h-16 mx-auto mb-4 text-primary-400" />
            <h3 className="text-xl font-semibold text-primary-100 mb-2">Select a conversation</h3>
            <p className="text-primary-300">Choose a conversation to start messaging</p>
          </div>
        </div>
      );
    }

    const typingUsersInConversation = typingUsers.filter(t => t.conversationId === selectedConversation);

    // Virtual scrolling calculations
    const visibleStartIndex = Math.floor(scrollTop / MESSAGE_HEIGHT);
    const visibleEndIndex = Math.min(
      conversationMessages.length - 1,
      Math.ceil((scrollTop + containerHeight) / MESSAGE_HEIGHT)
    );
    const startIndex = Math.max(0, visibleStartIndex - OVERSCAN);
    const endIndex = Math.min(conversationMessages.length - 1, visibleEndIndex + OVERSCAN);
    const visibleMessages = conversationMessages.slice(startIndex, endIndex + 1);
    const totalHeight = conversationMessages.length * MESSAGE_HEIGHT;
    const offsetY = startIndex * MESSAGE_HEIGHT;

    return (
      <div className="flex-1 flex flex-col h-full overflow-hidden">
        {/* Chat Header - Fixed */}
        <div className="flex-shrink-0 h-16 px-4 py-3 border-b border-primary-700/30 bg-primary-900/95 backdrop-blur-sm flex items-center justify-between z-10">
          <div className="flex items-center space-x-3">
            <button
              onClick={() => setSelectedConversation(null)}
              className="lg:hidden p-2 hover:bg-primary-800/50 rounded-full transition-colors"
            >
              <ArrowLeft className="w-5 h-5 text-primary-300" />
            </button>
            
            <div className="w-10 h-10 bg-primary-800/50 rounded-full flex items-center justify-center">
              {selectedConversationUser.avatar ? (
                <img
                  src={selectedConversationUser.avatar}
                  alt={selectedConversationUser.username}
                  className="w-full h-full object-cover rounded-full"
                />
              ) : (
                <User className="w-5 h-5 text-primary-300" />
              )}
            </div>
            
            <div>
              <h3 className="font-semibold text-primary-100">
                @{selectedConversationUser.username}
              </h3>
              {typingUsersInConversation.length > 0 && (
                <p className="text-sm text-blue-400">typing...</p>
              )}
            </div>
          </div>
          
          <div className="flex items-center space-x-2">
            <button className="p-2 hover:bg-primary-800/50 rounded-full transition-colors">
              <Phone className="w-5 h-5 text-primary-300" />
            </button>
            <button className="p-2 hover:bg-primary-800/50 rounded-full transition-colors">
              <Video className="w-5 h-5 text-primary-300" />
            </button>
            <button className="p-2 hover:bg-primary-800/50 rounded-full transition-colors">
              <Info className="w-5 h-5 text-primary-300" />
            </button>
          </div>
        </div>

        {/* Messages Area - Scrollable with virtual rendering */}
        <div 
          ref={messagesContainerRef}
          className="flex-1 overflow-y-auto scrollbar-hide"
          style={{ 
            scrollbarWidth: 'none', 
            msOverflowStyle: 'none'
          }}
          onScroll={handleScroll}
        >
          {conversationMessages.length === 0 ? (
            <div className="h-full flex items-center justify-center p-4">
              <div className="text-center">
                <p className="text-primary-300">No messages yet</p>
                <p className="text-sm text-primary-400 mt-1">Send a message to start the conversation</p>
              </div>
            </div>
          ) : (
            <div style={{ height: totalHeight, position: 'relative' }}>
              <div style={{ transform: `translateY(${offsetY}px)` }}>
                {visibleMessages.map((message, index) => {
                  const actualIndex = startIndex + index;
                  // Handle populated user objects
                  const messageSenderId = typeof message.senderId === 'string' 
                    ? message.senderId 
                    : (message.senderId as any)?.id || (message.senderId as any)?._id;
                  const prevMessageSenderId = actualIndex > 0 
                    ? (typeof conversationMessages[actualIndex - 1].senderId === 'string' 
                        ? conversationMessages[actualIndex - 1].senderId 
                        : (conversationMessages[actualIndex - 1].senderId as any)?.id || (conversationMessages[actualIndex - 1].senderId as any)?._id)
                    : null;
                  const nextMessageSenderId = actualIndex < conversationMessages.length - 1 
                    ? (typeof conversationMessages[actualIndex + 1].senderId === 'string' 
                        ? conversationMessages[actualIndex + 1].senderId 
                        : (conversationMessages[actualIndex + 1].senderId as any)?.id || (conversationMessages[actualIndex + 1].senderId as any)?._id)
                    : null;

                  const isOwn = messageSenderId === state.currentUser?.id;
                  const showAvatar = actualIndex === 0 || prevMessageSenderId !== messageSenderId;
                  const isLastInGroup = actualIndex === conversationMessages.length - 1 || nextMessageSenderId !== messageSenderId;
                  
                  return (
                    <div
                      key={message.id}
                      className={`flex p-4 ${isOwn ? 'justify-end' : 'justify-start'}`}
                      style={{ minHeight: MESSAGE_HEIGHT }}
                    >
                      <div className={`flex items-end space-x-2 max-w-xs lg:max-w-md ${isOwn ? 'flex-row-reverse space-x-reverse' : ''}`}>
                        {/* Avatar */}
                        <div className="w-8 h-8 flex-shrink-0">
                          {showAvatar && !isOwn && (
                            <div className="w-8 h-8 bg-primary-800/50 rounded-full flex items-center justify-center">
                              {selectedConversationUser.avatar ? (
                                <img
                                  src={selectedConversationUser.avatar}
                                  alt={selectedConversationUser.username}
                                  className="w-full h-full object-cover rounded-full"
                                />
                              ) : (
                                <User className="w-4 h-4 text-primary-300" />
                              )}
                            </div>
                          )}
                        </div>
                        
                        {/* Message Bubble */}
                        <div
                          className={`px-4 py-2 rounded-2xl ${
                            isOwn
                              ? 'bg-blue-600 text-white'
                              : 'bg-primary-700/50 text-primary-100'
                          } ${
                            isLastInGroup ? '' : 'mb-1'
                          }`}
                        >
                          <p className="text-sm whitespace-pre-wrap">{message.content}</p>
                          
                          {/* Message metadata */}
                          <div className={`flex items-center justify-end mt-1 space-x-1 ${
                            isOwn ? 'text-blue-200' : 'text-primary-400'
                          }`}>
                            <span className="text-xs">
                              {formatMessageTime(message.timestamp)}
                            </span>
                            {isOwn && (
                              <div className="flex items-center">
                                {message.read ? (
                                  <CheckCheck className="w-3 h-3" />
                                ) : (
                                  <Check className="w-3 h-3" />
                                )}
                              </div>
                            )}
                          </div>
                        </div>
                      </div>
                    </div>
                  );
                })}
                
                {/* Typing Indicator */}
                {typingUsersInConversation.length > 0 && (
                  <div className="flex items-end space-x-2 p-4">
                    <div className="w-8 h-8 bg-primary-800/50 rounded-full flex items-center justify-center">
                      <User className="w-4 h-4 text-primary-300" />
                    </div>
                    <div className="bg-primary-700/50 px-4 py-2 rounded-2xl">
                      <div className="flex space-x-1">
                        <div className="w-2 h-2 bg-primary-400 rounded-full animate-bounce" style={{ animationDelay: '0ms' }} />
                        <div className="w-2 h-2 bg-primary-400 rounded-full animate-bounce" style={{ animationDelay: '150ms' }} />
                        <div className="w-2 h-2 bg-primary-400 rounded-full animate-bounce" style={{ animationDelay: '300ms' }} />
                      </div>
                    </div>
                  </div>
                )}
              </div>
              <div ref={messagesEndRef} />
            </div>
          )}
        </div>

        {/* Message Input - Fixed */}
        <div className="flex-shrink-0 p-4 border-t border-primary-700/30 bg-primary-900/95 backdrop-blur-sm">
          <div className="flex items-center space-x-3">
            <div className="flex-1">
              <textarea
                value={messageText}
                onChange={(e) => handleTyping(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Type a message..."
                className="message-input w-full px-4 py-3 bg-primary-800/50 border border-primary-700/30 rounded-xl text-primary-100 placeholder-primary-400 resize-none focus:outline-none focus:ring-2 focus:ring-blue-500/50 focus:border-transparent transition-all"
                rows={1}
                disabled={loading}
                style={{ 
                  minHeight: '48px',
                  maxHeight: '120px',
                  lineHeight: '1.5',
                  paddingTop: '12px',
                  paddingBottom: '12px'
                }}
                onInput={(e) => {
                  const target = e.target as HTMLTextAreaElement;
                  target.style.height = '48px';
                  target.style.height = Math.min(target.scrollHeight, 120) + 'px';
                }}
              />
            </div>
            <button
              onClick={handleSendMessage}
              disabled={!messageText.trim() || loading}
              className={`p-3 rounded-xl transition-all flex-shrink-0 ${
                messageText.trim() && !loading
                  ? 'bg-blue-600 hover:bg-blue-700 text-white shadow-lg hover:shadow-blue-500/25'
                  : 'bg-primary-700/50 text-primary-400 cursor-not-allowed'
              }`}
            >
              <Send className="w-5 h-5" />
            </button>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="h-full flex">
      {/* Mobile: Show either conversations list or chat */}
      <div className="lg:hidden w-full h-full">
        {selectedConversation ? renderChatInterface() : renderConversationsList()}
      </div>
      
      {/* Desktop: Show both side by side */}
      <div className="hidden lg:flex w-full h-full">
        {renderConversationsList()}
        {renderChatInterface()}
      </div>
    </div>
  );
}
