import React, { createContext, useContext, useReducer, useEffect, ReactNode } from 'react';
import { AppState, User, Vehicle, Listing, Trade, Message, Conversation, NavigationTab } from '../types/index.ts';
import { AuthService } from '../services/authService.ts';
import { DataService } from '../services/dataService.ts';

interface AppContextType {
  state: AppState;
  dispatch: React.Dispatch<AppAction>;
  login: (username: string, password: string) => Promise<{ success: boolean; error?: string }>;
  register: (userData: Omit<User, 'id' | 'createdAt'>) => Promise<{ success: boolean; error?: string }>;
  logout: () => void;
  addVehicle: (vehicle: Omit<Vehicle, 'id' | 'createdAt' | 'updatedAt'>) => void;
  updateVehicle: (vehicle: Vehicle) => void;
  deleteVehicle: (vehicleId: string) => void;
  addListing: (listing: Omit<Listing, 'id' | 'createdAt' | 'updatedAt' | 'views'>) => void;
  updateListing: (listing: Listing) => void;
  deleteListing: (listingId: string) => void;
  sendMessage: (message: Omit<Message, 'id' | 'timestamp' | 'read'>) => void;
  markMessagesAsRead: (conversationId: string) => void;
  addTrade: (trade: Omit<Trade, 'id' | 'createdAt' | 'updatedAt'>) => void;
  updateTrade: (trade: Trade) => void;
  activeTab: NavigationTab;
  setActiveTab: (tab: NavigationTab) => void;
}

type AppAction =
  | { type: 'SET_USER'; payload: User | null }
  | { type: 'SET_LOADING'; payload: boolean }
  | { type: 'SET_ERROR'; payload: string | null }
  | { type: 'SET_VEHICLES'; payload: Vehicle[] }
  | { type: 'ADD_VEHICLE'; payload: Vehicle }
  | { type: 'UPDATE_VEHICLE'; payload: Vehicle }
  | { type: 'DELETE_VEHICLE'; payload: string }
  | { type: 'SET_LISTINGS'; payload: Listing[] }
  | { type: 'ADD_LISTING'; payload: Listing }
  | { type: 'UPDATE_LISTING'; payload: Listing }
  | { type: 'DELETE_LISTING'; payload: string }
  | { type: 'SET_TRADES'; payload: Trade[] }
  | { type: 'ADD_TRADE'; payload: Trade }
  | { type: 'UPDATE_TRADE'; payload: Trade }
  | { type: 'SET_MESSAGES'; payload: Message[] }
  | { type: 'ADD_MESSAGE'; payload: Message }
  | { type: 'SET_CONVERSATIONS'; payload: Conversation[] }
  | { type: 'SET_ACTIVE_TAB'; payload: NavigationTab };

const initialState: AppState = {
  currentUser: null,
  vehicles: [],
  listings: [],
  trades: [],
  messages: [],
  conversations: [],
  isAuthenticated: false,
  loading: false,
  error: null,
};

function appReducer(state: AppState, action: AppAction): AppState {
  switch (action.type) {
    case 'SET_USER':
      return {
        ...state,
        currentUser: action.payload,
        isAuthenticated: action.payload !== null,
      };
    case 'SET_LOADING':
      return { ...state, loading: action.payload };
    case 'SET_ERROR':
      return { ...state, error: action.payload };
    case 'SET_VEHICLES':
      return { ...state, vehicles: action.payload };
    case 'ADD_VEHICLE':
      return { ...state, vehicles: [...state.vehicles, action.payload] };
    case 'UPDATE_VEHICLE':
      return {
        ...state,
        vehicles: state.vehicles.map(v => v.id === action.payload.id ? action.payload : v),
      };
    case 'DELETE_VEHICLE':
      return {
        ...state,
        vehicles: state.vehicles.filter(v => v.id !== action.payload),
      };
    case 'SET_LISTINGS':
      return { ...state, listings: action.payload };
    case 'ADD_LISTING':
      return { ...state, listings: [...state.listings, action.payload] };
    case 'UPDATE_LISTING':
      return {
        ...state,
        listings: state.listings.map(l => l.id === action.payload.id ? action.payload : l),
      };
    case 'DELETE_LISTING':
      return {
        ...state,
        listings: state.listings.filter(l => l.id !== action.payload),
      };
    case 'SET_TRADES':
      return { ...state, trades: action.payload };
    case 'ADD_TRADE':
      return { ...state, trades: [...state.trades, action.payload] };
    case 'UPDATE_TRADE':
      return {
        ...state,
        trades: state.trades.map(t => t.id === action.payload.id ? action.payload : t),
      };
    case 'SET_MESSAGES':
      return { ...state, messages: action.payload };
    case 'ADD_MESSAGE':
      return { ...state, messages: [...state.messages, action.payload] };
    case 'SET_CONVERSATIONS':
      return { ...state, conversations: action.payload };
    default:
      return state;
  }
}

const AppContext = createContext<AppContextType | undefined>(undefined);

interface AppProviderProps {
  children: ReactNode;
}

export function AppProvider({ children }: AppProviderProps) {
  const [state, dispatch] = useReducer(appReducer, initialState);
  const [activeTab, setActiveTab] = React.useState<NavigationTab>('garage');

  useEffect(() => {
    // Initialize app state
    const currentUser = AuthService.getCurrentUser();
    if (currentUser) {
      dispatch({ type: 'SET_USER', payload: currentUser });
      loadUserData(currentUser.id);
    }
  }, []);

  const loadUserData = (userId: string) => {
    dispatch({ type: 'SET_VEHICLES', payload: DataService.getUserVehicles(userId) });
    dispatch({ type: 'SET_LISTINGS', payload: DataService.getUserListings(userId) });
    dispatch({ type: 'SET_TRADES', payload: DataService.getUserTrades(userId) });
    dispatch({ type: 'SET_MESSAGES', payload: DataService.getUserMessages(userId) });
    dispatch({ type: 'SET_CONVERSATIONS', payload: DataService.getUserConversations(userId) });
  };

  const login = async (username: string, password: string, rememberMe: boolean = false) => {
    dispatch({ type: 'SET_LOADING', payload: true });
    dispatch({ type: 'SET_ERROR', payload: null });

    const result = await AuthService.login(username, password, rememberMe);
    
    if (result.success && result.user) {
      dispatch({ type: 'SET_USER', payload: result.user });
      loadUserData(result.user.id);
    } else {
      dispatch({ type: 'SET_ERROR', payload: result.error || 'Login failed' });
    }

    dispatch({ type: 'SET_LOADING', payload: false });
    return { success: result.success, error: result.error };
  };

  const register = async (userData: Omit<User, 'id' | 'createdAt'>) => {
    dispatch({ type: 'SET_LOADING', payload: true });
    dispatch({ type: 'SET_ERROR', payload: null });

    const result = await AuthService.register(userData);
    
    if (result.success && result.user) {
      dispatch({ type: 'SET_USER', payload: result.user });
      loadUserData(result.user.id);
    } else {
      dispatch({ type: 'SET_ERROR', payload: result.error || 'Registration failed' });
    }

    dispatch({ type: 'SET_LOADING', payload: false });
    return { success: result.success, error: result.error };
  };

  const logout = () => {
    AuthService.logout();
    dispatch({ type: 'SET_USER', payload: null });
    dispatch({ type: 'SET_VEHICLES', payload: [] });
    dispatch({ type: 'SET_LISTINGS', payload: [] });
    dispatch({ type: 'SET_TRADES', payload: [] });
    dispatch({ type: 'SET_MESSAGES', payload: [] });
    dispatch({ type: 'SET_CONVERSATIONS', payload: [] });
    setActiveTab('garage');
  };

  const addVehicle = (vehicleData: Omit<Vehicle, 'id' | 'createdAt' | 'updatedAt'>) => {
    if (!state.currentUser) return;

    const newVehicle: Vehicle = {
      ...vehicleData,
      id: DataService.generateId(),
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString(),
    };

    DataService.addVehicle(newVehicle);
    dispatch({ type: 'ADD_VEHICLE', payload: newVehicle });
  };

  const updateVehicle = (updatedVehicle: Vehicle) => {
    const vehicle = { ...updatedVehicle, updatedAt: new Date().toISOString() };
    DataService.updateVehicle(vehicle);
    dispatch({ type: 'UPDATE_VEHICLE', payload: vehicle });
  };

  const deleteVehicle = (vehicleId: string) => {
    DataService.deleteVehicle(vehicleId);
    dispatch({ type: 'DELETE_VEHICLE', payload: vehicleId });
  };

  const addListing = (listingData: Omit<Listing, 'id' | 'createdAt' | 'updatedAt' | 'views'>) => {
    if (!state.currentUser) return;

    const newListing: Listing = {
      ...listingData,
      id: DataService.generateId(),
      views: 0,
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString(),
    };

    DataService.addListing(newListing);
    dispatch({ type: 'ADD_LISTING', payload: newListing });
  };

  const updateListing = (updatedListing: Listing) => {
    const listing = { ...updatedListing, updatedAt: new Date().toISOString() };
    DataService.updateListing(listing);
    dispatch({ type: 'UPDATE_LISTING', payload: listing });
  };

  const deleteListing = (listingId: string) => {
    DataService.deleteListing(listingId);
    dispatch({ type: 'DELETE_LISTING', payload: listingId });
  };

  const sendMessage = (messageData: Omit<Message, 'id' | 'timestamp' | 'read'>) => {
    const newMessage: Message = {
      ...messageData,
      id: DataService.generateId(),
      timestamp: new Date().toISOString(),
      read: false,
    };

    DataService.addMessage(newMessage);
    dispatch({ type: 'ADD_MESSAGE', payload: newMessage });
    
    // Reload conversations
    if (state.currentUser) {
      dispatch({ type: 'SET_CONVERSATIONS', payload: DataService.getUserConversations(state.currentUser.id) });
    }
  };

  const markMessagesAsRead = (conversationId: string) => {
    if (!state.currentUser) return;
    
    DataService.markMessagesAsRead(conversationId, state.currentUser.id);
    dispatch({ type: 'SET_MESSAGES', payload: DataService.getUserMessages(state.currentUser.id) });
    dispatch({ type: 'SET_CONVERSATIONS', payload: DataService.getUserConversations(state.currentUser.id) });
  };

  const addTrade = (tradeData: Omit<Trade, 'id' | 'createdAt' | 'updatedAt'>) => {
    const newTrade: Trade = {
      ...tradeData,
      id: DataService.generateId(),
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString(),
    };

    DataService.addTrade(newTrade);
    dispatch({ type: 'ADD_TRADE', payload: newTrade });
  };

  const updateTrade = (updatedTrade: Trade) => {
    const trade = { ...updatedTrade, updatedAt: new Date().toISOString() };
    DataService.updateTrade(trade);
    dispatch({ type: 'UPDATE_TRADE', payload: trade });
  };

  const value: AppContextType = {
    state,
    dispatch,
    login,
    register,
    logout,
    addVehicle,
    updateVehicle,
    deleteVehicle,
    addListing,
    updateListing,
    deleteListing,
    sendMessage,
    markMessagesAsRead,
    addTrade,
    updateTrade,
    activeTab,
    setActiveTab,
  };

  return <AppContext.Provider value={value}>{children}</AppContext.Provider>;
}

export function useApp() {
  const context = useContext(AppContext);
  if (context === undefined) {
    throw new Error('useApp must be used within an AppProvider');
  }
  return context;
}
