import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Search, Filter, Eye, Heart, MapPin, Calendar, DollarSign, Car, User, MessageCircle } from 'lucide-react';
import { useApp } from '../../context/AppContext.tsx';
import { Listing, Vehicle } from '../../types/index.ts';
import { ListingDetailView } from './ListingDetailView.tsx';

export function ListingsView() {
  const { state } = useApp();
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedMake, setSelectedMake] = useState('');
  const [priceRange, setPriceRange] = useState({ min: '', max: '' });
  const [sortBy, setSortBy] = useState('newest');
  const [showFilters, setShowFilters] = useState(false);
  const [selectedListing, setSelectedListing] = useState<any>(null);

  // Get all active listings with their vehicle data
  const listingsWithVehicles = state.listings
    .filter(listing => listing.isActive)
    .map(listing => {
      const vehicle = state.vehicles.find(v => v.id === listing.vehicleId);
      const seller = state.currentUser; // In a real app, this would fetch the actual seller data
      return {
        ...listing,
        vehicle,
        seller: {
          username: seller?.username || 'Unknown',
          location: 'Location not specified'
        }
      };
    })
    .filter(item => item.vehicle); // Only include listings with valid vehicles

  const filteredListings = listingsWithVehicles.filter(listing => {
    if (!listing.vehicle) return false;
    
    const matchesSearch = listing.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         listing.vehicle.make.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         listing.vehicle.model.toLowerCase().includes(searchTerm.toLowerCase());
    
    const matchesMake = !selectedMake || listing.vehicle.make === selectedMake;
    
    const matchesPrice = (!priceRange.min || listing.price >= Number(priceRange.min)) &&
                        (!priceRange.max || listing.price <= Number(priceRange.max));
    
    return matchesSearch && matchesMake && matchesPrice;
  });

  const sortedListings = [...filteredListings].sort((a, b) => {
    switch (sortBy) {
      case 'price-low':
        return a.price - b.price;
      case 'price-high':
        return b.price - a.price;
      case 'mileage':
        return (a.vehicle?.mileage || 0) - (b.vehicle?.mileage || 0);
      case 'year':
        return (b.vehicle?.year || 0) - (a.vehicle?.year || 0);
      default: // newest
        return new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime();
    }
  });

  const makes = Array.from(new Set(listingsWithVehicles.map(l => l.vehicle?.make).filter(Boolean))).sort();

  const handleContactSeller = (listing: any) => {
    // TODO: Implement messaging functionality
    alert(`Contact seller: ${listing.seller.username}`);
  };

  const formatTimeAgo = (dateString: string) => {
    const now = new Date();
    const date = new Date(dateString);
    const diffInHours = Math.floor((now.getTime() - date.getTime()) / (1000 * 60 * 60));
    
    if (diffInHours < 1) return 'Just now';
    if (diffInHours < 24) return `${diffInHours}h ago`;
    const diffInDays = Math.floor(diffInHours / 24);
    if (diffInDays < 7) return `${diffInDays}d ago`;
    return date.toLocaleDateString();
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-primary-100">Vehicle Listings</h1>
          <p className="text-primary-300 mt-1">
            Browse and search through available vehicles
          </p>
        </div>
        <div className="text-right">
          <p className="text-2xl font-bold text-primary-100">{sortedListings.length}</p>
          <p className="text-sm text-primary-300">listings found</p>
        </div>
      </div>

      {/* Search and Filters */}
      <div className="glass-effect rounded-xl p-6">
        <div className="flex flex-col lg:flex-row gap-4">
          {/* Search Bar */}
          <div className="flex-1 relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-primary-400" />
            <input
              type="text"
              placeholder="Search by make, model, or title..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="input-field pl-10"
            />
          </div>

          {/* Filter Toggle */}
          <motion.button
            whileTap={{ scale: 0.95 }}
            onClick={() => setShowFilters(!showFilters)}
            className={`btn-secondary flex items-center space-x-2 ${showFilters ? 'bg-primary-100/20' : ''}`}
          >
            <Filter className="w-5 h-5" />
            <span>Filters</span>
          </motion.button>
        </div>

        {/* Expanded Filters */}
        {showFilters && (
          <motion.div
            initial={{ opacity: 0, height: 0 }}
            animate={{ opacity: 1, height: 'auto' }}
            exit={{ opacity: 0, height: 0 }}
            className="mt-4 pt-4 border-t border-primary-700/30"
          >
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
              {/* Make Filter */}
              <div>
                <label className="block text-sm font-medium text-primary-300 mb-2">
                  Make
                </label>
                <select
                  value={selectedMake}
                  onChange={(e) => setSelectedMake(e.target.value)}
                  className="input-field"
                >
                  <option value="">All Makes</option>
                  {makes.map(make => (
                    <option key={make} value={make}>{make}</option>
                  ))}
                </select>
              </div>

              {/* Price Range */}
              <div>
                <label className="block text-sm font-medium text-primary-300 mb-2">
                  Min Price
                </label>
                <input
                  type="number"
                  placeholder="$0"
                  value={priceRange.min}
                  onChange={(e) => setPriceRange(prev => ({ ...prev, min: e.target.value }))}
                  className="input-field"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-primary-300 mb-2">
                  Max Price
                </label>
                <input
                  type="number"
                  placeholder="No limit"
                  value={priceRange.max}
                  onChange={(e) => setPriceRange(prev => ({ ...prev, max: e.target.value }))}
                  className="input-field"
                />
              </div>

              {/* Sort By */}
              <div>
                <label className="block text-sm font-medium text-primary-300 mb-2">
                  Sort By
                </label>
                <select
                  value={sortBy}
                  onChange={(e) => setSortBy(e.target.value)}
                  className="input-field"
                >
                  <option value="newest">Newest First</option>
                  <option value="price-low">Price: Low to High</option>
                  <option value="price-high">Price: High to Low</option>
                  <option value="mileage">Lowest Mileage</option>
                  <option value="year">Newest Year</option>
                </select>
              </div>
            </div>
          </motion.div>
        )}
      </div>

      {/* Listings Grid */}
      {sortedListings.length === 0 ? (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="text-center py-16"
        >
          <div className="w-24 h-24 bg-primary-800/50 rounded-full flex items-center justify-center mx-auto mb-4">
            <Car className="w-12 h-12 text-primary-400" />
          </div>
          <h3 className="text-xl font-semibold text-primary-200 mb-2">No listings found</h3>
          <p className="text-primary-400 mb-6">
            {state.listings.length === 0 
              ? "No vehicles are currently listed for sale"
              : "Try adjusting your search criteria"
            }
          </p>
        </motion.div>
      ) : (
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          <AnimatePresence>
            {sortedListings.map((listing, index) => (
              <motion.div
                key={listing.id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, scale: 0.95 }}
                transition={{ delay: index * 0.05 }}
                className="glass-effect rounded-xl overflow-hidden hover:shadow-xl transition-all duration-300"
              >
                {/* Vehicle Image */}
                <div className="w-full h-48 bg-gradient-to-br from-primary-800/30 to-primary-700/30 flex items-center justify-center border-b border-primary-600/20 relative">
                  {listing.vehicle?.images && listing.vehicle.images.length > 0 ? (
                    <img
                      src={listing.vehicle.images[0]}
                      alt={listing.title}
                      className="w-full h-full object-cover"
                    />
                  ) : (
                    <Car className="w-16 h-16 text-primary-400" />
                  )}
                  
                  {/* Price Badge */}
                  <div className="absolute top-3 right-3 bg-green-500/90 text-white px-3 py-1 rounded-full font-bold">
                    ${listing.price.toLocaleString()}
                  </div>
                </div>

                <div className="p-6">
                  {/* Listing Title */}
                  <h3 className="text-xl font-bold text-primary-100 mb-2 line-clamp-2">
                    {listing.title}
                  </h3>

                  {/* Vehicle Details */}
                  {listing.vehicle && (
                    <div className="flex items-center justify-between text-sm text-primary-400 mb-3">
                      <div className="flex items-center space-x-4">
                        <span>{listing.vehicle.year}</span>
                        <span>•</span>
                        <span>{listing.vehicle.mileage.toLocaleString()} mi</span>
                        <span>•</span>
                        <span className="capitalize">{listing.vehicle.transmission}</span>
                      </div>
                    </div>
                  )}

                  {/* Description */}
                  <p className="text-primary-300 text-sm mb-4 line-clamp-2">
                    {listing.description}
                  </p>

                  {/* Tags */}
                  {listing.tags && listing.tags.length > 0 && (
                    <div className="flex flex-wrap gap-1 mb-4">
                      {listing.tags.slice(0, 3).map((tag, tagIndex) => (
                        <span
                          key={tagIndex}
                          className="bg-primary-100/10 text-primary-200 px-2 py-1 rounded-full text-xs"
                        >
                          {tag}
                        </span>
                      ))}
                      {listing.tags.length > 3 && (
                        <span className="bg-primary-100/10 text-primary-200 px-2 py-1 rounded-full text-xs">
                          +{listing.tags.length - 3} more
                        </span>
                      )}
                    </div>
                  )}

                  {/* Problems */}
                  {listing.problems && listing.problems.length > 0 && (
                    <div className="mb-4">
                      <p className="text-yellow-400 text-xs font-medium mb-1">Known Issues:</p>
                      <div className="space-y-1">
                        {listing.problems.slice(0, 2).map((problem, problemIndex) => (
                          <p key={problemIndex} className="text-yellow-300 text-xs">
                            • {problem}
                          </p>
                        ))}
                        {listing.problems.length > 2 && (
                          <p className="text-yellow-300 text-xs">
                            • +{listing.problems.length - 2} more issues
                          </p>
                        )}
                      </div>
                    </div>
                  )}

                  {/* Footer */}
                  <div className="flex items-center justify-between pt-4 border-t border-primary-700/30">
                    {/* Seller Info */}
                    <div className="flex items-center space-x-2">
                      <div className="w-8 h-8 bg-primary-800/50 rounded-full flex items-center justify-center">
                        <User className="w-4 h-4 text-primary-300" />
                      </div>
                      <div>
                        <p className="text-sm font-medium text-primary-200">
                          {listing.seller.username}
                        </p>
                        <p className="text-xs text-primary-400">
                          {formatTimeAgo(listing.createdAt)}
                        </p>
                      </div>
                    </div>

                    {/* Actions */}
                    <div className="flex items-center space-x-2">
                      <div className="flex items-center space-x-1 text-primary-400">
                        <Eye className="w-4 h-4" />
                        <span className="text-xs">{listing.views}</span>
                      </div>
                      <motion.button
                        whileHover={{ scale: 1.02 }}
                        whileTap={{ scale: 0.98 }}
                        onClick={() => handleContactSeller(listing)}
                        className="btn-primary flex items-center space-x-1 text-sm px-3 py-1"
                      >
                        <MessageCircle className="w-4 h-4" />
                        <span>Contact</span>
                      </motion.button>
                    </div>
                  </div>
                </div>
              </motion.div>
            ))}
          </AnimatePresence>
        </div>
      )}
    </div>
  );
}
